        // Funci√≥n para procesar pagos
        window.processPayment = function(method) {
            console.log('üí≥ Procesando pago con m√©todo:', method);
            
            const cartData = localStorage.getItem('carrito');
            const cart = JSON.parse(cartData || '[]');
            
            if (!cart || cart.length === 0) {
                alert('‚ùå El carrito est√° vac√≠o');
                return;
            }
            
            // Mostrar loading
            const container = document.getElementById('mercadopago-payment-container');
            if (container) {
                container.innerHTML = `
                    <div class="text-center py-4">
                        <div class="spinner-border text-primary mb-3" role="status"></div>
                        <h6>Procesando pago...</h6>
                        <p class="text-muted">Creando preferencia de MercadoPago...</p>
                    </div>
                `;
            }
            
            // Crear preferencia real con MercadoPago
            createMercadoPagoPreference(cart, method);
        };
        
        // Funci√≥n para crear preferencia real de MercadoPago
        async function createMercadoPagoPreference(cart, paymentMethod) {
            try {
                // Calcular totales
                const subtotal = cart.reduce((sum, item) => {
                    const price = parseFloat(item.price || item.precio) || 0;
                    const quantity = parseInt(item.quantity || item.cantidad) || 1;
                    return sum + (price * quantity);
                }, 0);
                
                const shipping = 0; // ENV√çO SIEMPRE GRATIS
                
                // Preparar items para MercadoPago
                const items = cart.map(item => ({
                    id: item.id || 'item_' + Date.now(),
                    title: `${item.name || item.nombre} - ${item.color} - Talla ${item.size || item.talla}`,
                    description: `${item.name || item.nombre} color ${item.color} talla ${item.size || item.talla}`,
                    quantity: parseInt(item.quantity || item.cantidad) || 1,
                    unit_price: parseFloat(item.price || item.precio) || 0,
                    currency_id: 'COP'
                }));
                
                // Agregar env√≠o
                if (shipping > 0) {
                    items.push({
                        id: 'shipping',
                        title: 'Env√≠o',
                        description: 'Costo de env√≠o nacional',
                        quantity: 1,
                        unit_price: shipping,
                        currency_id: 'COP'
                    });
                }
                
                // Datos de la preferencia
                const preferenceData = {
                    items: items,
                    payer: {
                        email: 'test@example.com'
                    },
                    back_urls: {
                        success: window.location.origin + '/Musa/success.html',
                        failure: window.location.origin + '/Musa/failure.html',
                        pending: window.location.origin + '/Musa/pending.html'
                    },
                    auto_return: 'approved',
                    external_reference: 'MUSA-' + Date.now() + '-' + Math.random().toString(36).substr(2, 9),
                    statement_descriptor: 'MUSA FASHION',
                    payment_methods: getPaymentMethodsConfig(paymentMethod)
                };
                
                console.log('üì¶ Enviando datos a API de MercadoPago:', preferenceData);
                
                // Llamar a la API para crear la preferencia
                const response = await fetch('/Musa/api/create-preference.php', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(preferenceData)
                });
                
                console.log('üîç Response status:', response.status);
                console.log('üîç Response ok:', response.ok);
                
                if (!response.ok) {
                    throw new Error(`HTTP Error: ${response.status} - ${response.statusText}`);
                }
                
                const result = await response.json();
                console.log('üîç Respuesta completa de la API:', result);
                
                if (result.success && result.init_point) {
                    console.log('‚úÖ Preferencia creada exitosamente');
                    showPaymentRedirect(result);
                } else {
                    throw new Error(result.error || 'Error desconocido al crear preferencia');
                }
                
            } catch (error) {
                console.error('‚ùå Error creando preferencia:', error);
                showPaymentError('Error al preparar el pago: ' + error.message);
            }
        }
        
        // Configuraci√≥n de m√©todos de pago seg√∫n la selecci√≥n
        function getPaymentMethodsConfig(method) {
            switch (method) {
                case 'credit_card':
                    return {
                        excluded_payment_types: [
                            { id: 'atm' },
                            { id: 'ticket' },
                            { id: 'bank_transfer' },
                            { id: 'debit_card' }
                        ],
                        installments: 12
                    };
                    
                case 'pse':
                    return {
                        excluded_payment_types: [
                            { id: 'credit_card' },
                            { id: 'debit_card' },
                            { id: 'atm' },
                            { id: 'ticket' }
                        ],
                        included_payment_methods: [
                            { id: 'pse' }
