        // =================== FUNCIONES DE EXTRACCI√ìN DE DATOS DEL PRODUCTO ===================
        
        // Extraer ID del producto desde diferentes fuentes
        function extractProductId(container, element) {
            try {
                // M√©todo 1: data-product-id
                if (container.dataset.productId) {
                    return container.dataset.productId;
                }
                
                // M√©todo 2: data-id  
                if (container.dataset.id) {
                    return container.dataset.id;
                }
                
                // M√©todo 3: desde la URL de la imagen
                const imgSrc = element.src;
                const matches = imgSrc.match(/product_([a-f0-9]+)_/);
                if (matches) {
                    return matches[1];
                }
                
                // M√©todo 4: desde clases CSS
                const classes = container.className;
                const idMatch = classes.match(/product-(\d+)/);
                if (idMatch) {
                    return idMatch[1];
                }
                
                return 'unknown';
            } catch (error) {
                console.error('‚ùå Error extrayendo ID:', error);
                return 'unknown';
            }
        }
        
        // Extraer t√≠tulo del producto
        function extractProductTitle(container, element) {
            try {
                // M√©todo 1: data-name
                if (container.dataset.name) {
                    return container.dataset.name;
                }
                
                // M√©todo 2: buscar en elementos del contenedor
                const titleSelectors = [
                    '.product-name',
                    '.card-title',
                    'h5',
                    'h4',
                    'h3',
                    '.product-title',
                    '.name'
                ];
                
                for (const selector of titleSelectors) {
                    const titleElement = container.querySelector(selector);
                    if (titleElement && titleElement.textContent.trim()) {
                        return titleElement.textContent.trim();
                    }
                }
                
                // M√©todo 3: desde alt de la imagen
                if (element.alt && element.alt.trim()) {
                    return element.alt.trim();
                }
                
                return 'Producto sin nombre';
            } catch (error) {
                console.error('‚ùå Error extrayendo t√≠tulo:', error);
                return 'Producto sin nombre';
            }
        }
        
        // Extraer precio del producto
        function extractProductPrice(container) {
            try {
                // M√©todo 1: data-price
                if (container.dataset.price) {
                    return container.dataset.price;
                }
                
                // M√©todo 2: buscar en elementos del contenedor
                const priceSelectors = [
                    '.product-price',
                    '.price',
                    '.current-price',
                    '.sale-price',
                    '.text-primary'
                ];
                
                for (const selector of priceSelectors) {
                    const priceElement = container.querySelector(selector);
                    if (priceElement && priceElement.textContent.trim()) {
                        return priceElement.textContent.trim();
                    }
                }
                
                return '$0';
            } catch (error) {
                console.error('‚ùå Error extrayendo precio:', error);
                return '$0';
            }
        }
        
        // Extraer precio anterior del producto
        function extractProductOldPrice(container) {
            try {
                const oldPriceSelectors = [
                    '.old-price',
                    '.original-price',
                    '.text-muted del',
                    'del',
                    '.crossed-price'
                ];
                
                for (const selector of oldPriceSelectors) {
