                    }
                }
                
                return null;
            } catch (error) {
                console.error('‚ùå Error extrayendo precio anterior:', error);
                return null;
            }
        }
        
        // Extraer categor√≠a del producto
        function extractProductCategory(container) {
            try {
                if (container.dataset.category) {
