        // Funci√≥n para obtener im√°genes adicionales del producto
        function getAdditionalProductImages(productData) {
            const additionalImages = [];
            
            try {
                console.log('üîç Buscando im√°genes adicionales para:', productData);
                
                // M√©todo 1: Desde el campo 'images' (separado por comas o saltos de l√≠nea)
                if (productData.images) {
                    if (typeof productData.images === 'string') {
                        const imagesList = productData.images.split(/[,\n]/).map(img => img.trim()).filter(img => img);
                        additionalImages.push(...imagesList);
                        console.log('üì∏ Im√°genes desde campo images:', imagesList.length);
                    }
                }
                
                // M√©todo 2: Desde el campo 'additional_images'
                if (productData.additional_images) {
                    if (typeof productData.additional_images === 'string') {
                        const additionalList = productData.additional_images.split(',').map(img => img.trim()).filter(img => img);
                        additionalImages.push(...additionalList);
                        console.log('üì∏ Im√°genes adicionales encontradas:', additionalList.length);
                    }
                }
                
                // M√©todo 3: Desde fullData si existe
                if (productData.fullData && productData.fullData.additional_images) {
                    const fullDataImages = productData.fullData.additional_images.split(',').map(img => img.trim()).filter(img => img);
                    additionalImages.push(...fullDataImages);
                    console.log('üì∏ Im√°genes desde fullData:', fullDataImages.length);
                }
                
                // M√©todo 4: Buscar en el DOM si existe informaci√≥n adicional
                if (productData.id) {
                    const productElement = document.querySelector(`[data-product-id="${productData.id}"]`);
                    if (productElement) {
                        const dataImages = productElement.getAttribute('data-images');
                        if (dataImages) {
                            const domImages = dataImages.split(',').map(img => img.trim()).filter(img => img);
                            additionalImages.push(...domImages);
                            console.log('üì∏ Im√°genes desde DOM:', domImages.length);
                        }
                    }
                }
                
                // Eliminar duplicados y normalizar rutas
                const uniqueImages = [...new Set(additionalImages)].map(img => {
                    // Normalizar ruta de imagen
                    if (img.startsWith('uploads/')) {
                        return img;
                    } else if (img.startsWith('./uploads/')) {
                        return img.substring(2);
                    } else if (!img.startsWith('http') && !img.startsWith('images/')) {
                        return `uploads/${img}`;
                    }
                    return img;
                });
                
                console.log(`üì∏ Total de im√°genes adicionales √∫nicas: ${uniqueImages.length}`);
                return uniqueImages;
                
            } catch (error) {
                console.error('‚ùå Error obteniendo im√°genes adicionales:', error);
                return [];
            }
        }
        
