        // Funci√≥n auxiliar para actualizar badge del carrito flotante
        function updateFloatingCartBadge(cart) {
            const totalItems = cart.reduce((total, item) => total + (parseInt(item.quantity) || 1), 0);
            
            // Actualizar badge del carrito flotante
            const floatingBadge = document.querySelector('.float-cart .cart-badge');
            if (floatingBadge) {
                floatingBadge.textContent = totalItems;
                floatingBadge.style.display = totalItems > 0 ? 'flex' : 'none';
            }
            
            // Actualizar otros badges tambi√©n
            const badges = document.querySelectorAll('.cart-badge, .badge[data-cart-count], #cart-count');
            badges.forEach(badge => {
                badge.textContent = totalItems;
                badge.style.display = totalItems > 0 ? 'inline-block' : 'none';
            });
        }
        
        // Funci√≥n auxiliar para actualizar contador exacto
        function updateCartCounterExact(cart) {
            const totalItems = cart.reduce((total, item) => total + (parseInt(item.quantity) || 1), 0);
            
            // Selectores exactos del sistema original
            const selectors = [
                '.cart-count', '.cart-counter', '.badge', '[data-cart-count]',
                '#cart-count', '#cartCount', '.cart-icon .badge', '.navbar .badge'
            ];
            
            selectors.forEach(selector => {
                try {
                    const elements = document.querySelectorAll(selector);
                    elements.forEach(element => {
                        if (element) {
                            element.textContent = totalItems;
                            element.style.display = totalItems > 0 ? 'inline' : 'none';
                        }
                    });
                } catch (e) {
                    // Silencioso si el selector falla
                }
            });
        }
        
