        // üîç FUNCI√ìN AVANZADA DE LUPA PARA MODAL DE PRODUCTO
        window.initAdvancedMagnifier = function() {
            console.log('üîç Inicializando lupa avanzada...');
            
            const imageWrapper = document.getElementById('imageZoomWrapper');
            const mainImage = document.getElementById('productViewImage');
            const magnifyingGlass = document.getElementById('magnifyingGlass');
            const magnifiedImage = document.getElementById('magnifiedImage');
            
            if (!imageWrapper || !mainImage || !magnifyingGlass || !magnifiedImage) {
                console.warn('‚ö†Ô∏è Elementos de lupa no encontrados');
                return;
            }
            
            // Configurar la imagen magnificada
            magnifiedImage.style.backgroundImage = `url(${mainImage.src})`;
            
            let isActive = false;
            
            // Eventos de mouse
            imageWrapper.addEventListener('mouseenter', function() {
                isActive = true;
                magnifyingGlass.classList.add('active');
                console.log('üîç Lupa activada');
            });
            
            imageWrapper.addEventListener('mouseleave', function() {
                isActive = false;
                magnifyingGlass.classList.remove('active');
                console.log('üîç Lupa desactivada');
            });
            
            imageWrapper.addEventListener('mousemove', function(e) {
                if (!isActive) return;
                
                const rect = imageWrapper.getBoundingClientRect();
                const x = e.clientX - rect.left;
                const y = e.clientY - rect.top;
                
                // Posicionar la lupa
                const glassSize = magnifyingGlass.offsetWidth;
                const halfGlass = glassSize / 2;
                
                // Mantener la lupa dentro de los l√≠mites
                let glassX = x - halfGlass;
                let glassY = y - halfGlass;
                
                glassX = Math.max(0, Math.min(glassX, rect.width - glassSize));
                glassY = Math.max(0, Math.min(glassY, rect.height - glassSize));
                
                magnifyingGlass.style.left = glassX + 'px';
                magnifyingGlass.style.top = glassY + 'px';
                
                // Calcular la posici√≥n de la imagen magnificada
                const imageX = (x / rect.width) * 100;
                const imageY = (y / rect.height) * 100;
                
                magnifiedImage.style.backgroundPosition = `${imageX}% ${imageY}%`;
            });
            
            // Actualizar la imagen cuando cambie
            const observer = new MutationObserver(function(mutations) {
                mutations.forEach(function(mutation) {
                    if (mutation.type === 'attributes' && mutation.attributeName === 'src') {
                        magnifiedImage.style.backgroundImage = `url(${mainImage.src})`;
                        console.log('üîç Imagen de lupa actualizada');
                    }
                });
            });
            
            observer.observe(mainImage, { attributes: true });
            
            console.log('‚úÖ Lupa avanzada inicializada correctamente');
        };
