        // Funci√≥n para calcular el total del carrito
        function calculateCartTotal() {
            const cart = JSON.parse(localStorage.getItem('carrito') || '[]');
            let total = 0;
            cart.forEach(item => {
                total += parseFloat(item.price || item.precio) * parseInt(item.quantity || item.cantidad);
            });
            // Agregar env√≠o
            const shipping = 0; // ENV√çO SIEMPRE GRATIS // Env√≠o gratis si subtotal >= $249.900
            total += shipping;
            return total;
        }
        
        // Funci√≥n para validar el formulario de pago
        function validatePaymentForm() {
            const requiredFields = [
                { id: 'cardNumber', name: 'N√∫mero de tarjeta' },
                { id: 'expiryDate', name: 'Fecha de expiraci√≥n' },
                { id: 'securityCode', name: 'CVC' },
                { id: 'cardholderName', name: 'Nombre del titular' },
                { id: 'email', name: 'Email' }
            ];
            
            let isValid = true;
            const errors = [];
            
            // Limpiar todos los errores visuales primero
            const allInputs = document.querySelectorAll('#simple-checkout-form input, #simple-checkout-form select');
            allInputs.forEach(input => {
                input.classList.remove('is-invalid');
                input.style.borderColor = '';
                const errorMsg = input.parentNode.querySelector('.invalid-feedback');
                if (errorMsg) {
                    errorMsg.remove();
                }
            });
            
            requiredFields.forEach(field => {
                const element = document.getElementById(field.id);
                if (!element || !element.value.trim()) {
                    isValid = false;
                    errors.push(field.name);
                    if (element) {
                        element.classList.add('is-invalid');
                        element.style.borderColor = '#dc3545';
                    }
                }
            });
            
            // Validaci√≥n espec√≠fica SOLO para el n√∫mero de tarjeta
            const cardNumber = document.getElementById('cardNumber');
            if (cardNumber && cardNumber.value) {
                const cleanCardNumber = cardNumber.value.replace(/\s/g, '');
                if (cleanCardNumber.length < 15 || cleanCardNumber.length > 19) {
                    isValid = false;
                    errors.push('N√∫mero de tarjeta debe tener entre 15 y 19 d√≠gitos');
                    cardNumber.classList.add('is-invalid');
                    cardNumber.style.borderColor = '#dc3545';
                }
            }
            
            // Validaci√≥n espec√≠fica para email
            const email = document.getElementById('email');
            if (email && email.value && !email.value.includes('@')) {
                isValid = false;
                errors.push('Email inv√°lido');
                email.classList.add('is-invalid');
                email.style.borderColor = '#dc3545';
            }
            
            if (!isValid) {
                alert('Por favor corrige los siguientes campos:\n‚Ä¢ ' + errors.join('\n‚Ä¢ '));
            }
            
            return isValid;
        }
        
        // Funci√≥n para limpiar todos los errores del formulario
        function clearAllFormErrors() {
            const allInputs = document.querySelectorAll('#simple-checkout-form input, #simple-checkout-form select');
            allInputs.forEach(input => {
                input.classList.remove('is-invalid');
                input.style.borderColor = '';
                const errorMsg = input.parentNode.querySelector('.invalid-feedback');
                if (errorMsg) {
                    errorMsg.remove();
                }
            });
            
            // Limpiar espec√≠ficamente cualquier mensaje de error flotante
            const errorMessages = document.querySelectorAll('.invalid-feedback, .error-message');
            errorMessages.forEach(msg => msg.remove());
        }
        
        // Funci√≥n para obtener datos del formulario - SIMPLIFICADA SIN DOCUMENTO
        function getPaymentFormData() {
            return {
                cardNumber: document.getElementById('cardNumber')?.value.replace(/\s/g, '') || '',
                expiryDate: document.getElementById('expiryDate')?.value || '',
                securityCode: document.getElementById('securityCode')?.value || '',
                cardholderName: document.getElementById('cardholderName')?.value || '',
                email: document.getElementById('email')?.value || '',
                installments: document.getElementById('installments')?.value || '1'
            };
        }
        
        // Funci√≥n para resetear el bot√≥n de MercadoPago
        function resetMercadoPagoButton(button) {
            if (!button) button = document.getElementById('mercadopago-button');
            if (button) {
                button.classList.remove('loading');
                button.disabled = false;
                button.innerHTML = '<i class="bi bi-credit-card me-2"></i>Pagar con MercadoPago';
            }
        }
        
        // Configurar el bot√≥n cuando se abra el modal
        document.addEventListener('DOMContentLoaded', function() {
            const paymentModal = document.getElementById('PaymentModal');
            if (paymentModal) {
                paymentModal.addEventListener('shown.bs.modal', function() {
                    setupMercadoPagoButton();
                });
            }
        });
    </script>
    
