        // Procesar pago con m√©todo espec√≠fico
        function processPayment(method) {
            console.log(`üîÑ Procesando pago con m√©todo: ${method}`);
            
            const container = document.getElementById('mercadopago-payment-container');
            
            // Mostrar loading espec√≠fico seg√∫n el m√©todo
            const methodNames = {
                'credit_card': 'Tarjeta de Cr√©dito',
                'debit_card': 'Tarjeta D√©bito',
                'pse': 'PSE',
                'efecty': 'Efecty',
                'all': 'MercadoPago'
            };
            
            const methodName = methodNames[method] || 'MercadoPago';
            
            container.innerHTML = `
                <div class="text-center py-4">
                    <div class="spinner-border text-primary mb-3" role="status"></div>
                    <h6>Procesando pago con ${methodName}...</h6>
                    <p class="text-muted">Preparando checkout seguro...</p>
                </div>
            `;

            // Crear preferencia con m√©todo espec√≠fico
            createMercadoPagoPreferenceWithMethod(method);
        }
        
        // Crear preferencia con m√©todo espec√≠fico
        async function createMercadoPagoPreferenceWithMethod(paymentMethod) {
            try {
                // Obtener datos del carrito y env√≠o
                const carrito = JSON.parse(localStorage.getItem('carrito') || '[]');
                const datosEnvio = JSON.parse(localStorage.getItem('datosEnvio') || '{}');
                
                if (carrito.length === 0) {
                    throw new Error('El carrito est√° vac√≠o');
                }
                
                // Calcular total
                const subtotal = carrito.reduce((sum, item) => sum + (item.price * item.quantity), 0);
                const envio = 0; // ENV√çO SIEMPRE GRATIS
                const total = subtotal + envio;
                
                // Preparar datos para MercadoPago
                const preferenceData = {
                    items: carrito.map(item => ({
                        title: item.name,
                        unit_price: item.price,
                        quantity: item.quantity,
                        currency_id: 'COP'
                    })),
                    payer: {
                        name: datosEnvio.nombre || 'Cliente',
                        email: datosEnvio.email || 'test@example.com',
                        phone: {
                            number: datosEnvio.telefono || '3001234567'
                        },
                        address: {
                            street_name: datosEnvio.direccion || 'Direcci√≥n no especificada',
                            zip_code: datosEnvio.codigoPostal || '11001'
                        }
                    },
                    payment_method: paymentMethod, // M√©todo espec√≠fico
                    back_urls: {
                        success: `${window.location.origin}/Musa/success.html`,
                        failure: `${window.location.origin}/Musa/failure.html`,
                        pending: `${window.location.origin}/Musa/pending.html`
                    },
                    external_reference: `MUSA-${Date.now()}`,
                    notification_url: `${window.location.origin}/Musa/api/webhook.php`
                };
                
                // Agregar env√≠o como item adicional si aplica
                if (envio > 0) {
                    preferenceData.items.push({
                        title: `Env√≠o ${datosEnvio.metodo}`,
                        unit_price: envio,
                        quantity: 1,
                        currency_id: 'COP'
                    });
                }
                
                console.log('üì¶ Datos de preferencia:', preferenceData);
                
                // Llamar a la API backend
                const response = await fetch('/Musa/api/create-preference.php', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(preferenceData)
                });
                
                const result = await response.json();
                
                if (result.success) {
                    console.log('‚úÖ Preferencia creada exitosamente para m√©todo:', paymentMethod);
                    
                    // Redirigir al checkout de MercadoPago
                    if (result.init_point) {
                        window.open(result.init_point, '_blank');
                        
                        // Mostrar mensaje de √©xito en el contenedor
                        const container = document.getElementById('mercadopago-payment-container');
                        container.innerHTML = `
                            <div class="alert alert-info text-center">
                                <i class="bi bi-arrow-up-right-square fs-2 text-primary"></i>
                                <h5 class="mt-3">Checkout abierto</h5>
                                <p>Se abri√≥ una nueva ventana para completar tu pago con ${paymentMethod === 'pse' ? 'PSE' : paymentMethod === 'efecty' ? 'Efecty' : 'tarjeta'}.</p>
                                <small class="text-muted">Si no se abri√≥ autom√°ticamente, 
                                    <a href="${result.init_point}" target="_blank">haz clic aqu√≠</a>
                                </small>
                            </div>
                        `;
                    }
                } else {
                    throw new Error(result.error || 'Error desconocido');
                }
                
            } catch (error) {
                console.error('‚ùå Error creando preferencia:', error);
                const container = document.getElementById('mercadopago-payment-container');
                container.innerHTML = `
                    <div class="alert alert-danger text-center">
                        <i class="bi bi-exclamation-triangle"></i>
                        <h6 class="mt-2">Error al procesar el pago</h6>
                        <p class="mb-3">${error.message}</p>
                        <button class="btn btn-outline-primary" onclick="initMercadoPago()">
                            <i class="bi bi-arrow-clockwise me-2"></i>Reintentar
                        </button>
                    </div>
                `;
            }
        }

        // Completar compra
        window.completePurchase = function() {
            // Limpiar carrito
            localStorage.removeItem('carrito');
            updateCartCounter();
            
            // Cerrar modal
            const modal = bootstrap.Modal.getInstance(document.getElementById('PaymentModal'));
            if (modal) modal.hide();
            
            // Mostrar confirmaci√≥n
            showNotification('‚úÖ ¬°Compra realizada exitosamente!', 'success');
        };

        // Volver al env√≠o
        window.backToShipping = function() {
            const paymentModal = bootstrap.Modal.getInstance(document.getElementById('PaymentModal'));
            const shippingModal = new bootstrap.Modal(document.getElementById('ShippingModal'));
            
            if (paymentModal) paymentModal.hide();
            setTimeout(() => shippingModal.show(), 300);
        };

        // Funci√≥n para mostrar errores
        function showError(message) {
            const container = document.getElementById('mercadopago-payment-container');
            if (container) {
                container.innerHTML = `
                    <div class="alert alert-danger text-center">
                        <i class="bi bi-exclamation-triangle"></i>
                        <p class="mb-0">${message}</p>
                    </div>
                `;
            }
        }

        console.log('‚úÖ Sistema de 3 pasos configurado');
        console.log('   - Paso 1: CartModal (productos)');
        console.log('   - Paso 2: ShippingModal (env√≠o)');
        console.log('   - Paso 3: PaymentModal (pago final)');
        
        // Verificar funciones cr√≠ticas
        console.log('üîç Verificando funciones:');
        console.log('  - proceedToPayment:', typeof window.proceedToPayment);
        console.log('  - proceedToPaymentFromShipping:', typeof window.proceedToPaymentFromShipping);
        console.log('  - updatePaymentSummary:', typeof window.updatePaymentSummary);
        console.log('  - initMercadoPago:', typeof window.initMercadoPago);

        // Forzar declaraciones globales cr√≠ticas
        if (typeof window.proceedToPayment === 'undefined') {
            console.error('‚ùå proceedToPayment no est√° definida, redefiniendo...');
            window.proceedToPayment = function() {
                console.log('üí≥ [FALLBACK] Iniciando flujo de 3 pasos...');
                const cart = JSON.parse(localStorage.getItem('carrito') || '[]');
                if (cart.length === 0) {
                    alert('‚ö†Ô∏è El carrito est√° vac√≠o.');
                    return;
                }
                const cartModal = document.getElementById('CartModal');
                if (cartModal) {
                    const bsModal = bootstrap.Modal.getInstance(cartModal);
                    if (bsModal) bsModal.hide();
                }
                setTimeout(() => {
                    const shippingModal = new bootstrap.Modal(document.getElementById('ShippingModal'));
                    shippingModal.show();
                }, 300);
            };
        }

        // Verificaci√≥n inmediata
        setTimeout(() => {
            console.log('üîç Verificando funciones:');
            console.log('  - proceedToPayment:', typeof window.proceedToPayment);
            console.log('  - proceedToPaymentFromShipping:', typeof window.proceedToPaymentFromShipping);
            console.log('  - updatePaymentSummary:', typeof window.updatePaymentSummary);
