        const allProducts = document.querySelectorAll('.product-card, .api-product, .main-product-card');
        let visibleCount = 0;
        let hiddenCount = 0;
        
        console.log(`üìä Productos encontrados: ${allProducts.length} (legacy + API + main)`);
        
        allProducts.forEach((product, index) => {
            const productCategory = (product.dataset.category || '').toLowerCase().trim();
            const productGender = (product.dataset.gender || '').toLowerCase().trim();
            const isApiProduct = product.classList.contains('api-product');
            const isMainProduct = product.classList.contains('main-product-card');
            
            // Debug espec√≠fico para productos API y Main
            if (isApiProduct || isMainProduct) {
                console.log(`üì¶ Producto ${isMainProduct ? 'MAIN' : 'API'} ${index + 1} - Cat: "${productCategory}", Gender: "${productGender}"`);
            }
            
            // L√≥gica de mostrar/ocultar mejorada para mapear categor√≠as
            const showAll = gender === 'all' && filter === 'all';
            
            // Mapeo inteligente de g√©nero
            let showGender = false;
            if (gender === 'all') {
                showGender = true;
            } else if (productGender === 'unisex') {
                showGender = true; // Los productos unisex se muestran para cualquier g√©nero
            } else if (productGender === gender) {
                showGender = true;
            }
            
            // Mapeo inteligente de categor√≠a
            let showCategory = false;
            if (filter === 'all') {
                showCategory = true;
            } else {
                // Manejar filtros undefined o null
                if (!filter || filter === 'undefined' || filter === undefined) {
                    showCategory = true;
                } else {
                    // Extraer la categor√≠a base del filtro (ej: "camisas-hombre" -> "camisas")
                    const filterParts = filter.toString().split('-');
                    const baseCategory = filterParts[0]; // "camisas", "chaquetas", etc.
                    const filterGender = filterParts[1]; // "hombre", "mujer"
                    
                    // Comparar categor√≠a base
                    if (productCategory === baseCategory) {
                        // Si el filtro tiene g√©nero espec√≠fico, verificar que coincida
                        if (filterGender) {
                            if (productGender === filterGender || productGender === 'unisex') {
                                showCategory = true;
                            }
                        } else {
                            showCategory = true;
                        }
                    }
                }
                }
                
                // Fallback: comparaci√≥n directa completa
                if (!showCategory && (productCategory === filter || productCategory.includes(filter) || filter.includes(productCategory))) {
                    showCategory = true;
                }
            }
            
            const shouldShow = showAll || (showGender && showCategory);
            
            // Aplicar visibilidad
            if (shouldShow) {
                // Para productos API y Main, forzar visibilidad EXTREMA
                if (isApiProduct || isMainProduct) {
