            } else {
                // Ocultar producto
                if (isApiProduct || isMainProduct) {
                    product.style.display = 'none';
                    product.classList.add('filtered-hidden', 'hidden');
                    product.classList.remove('filtered-visible', 'force-visible');
                } else {
