        console.log(`üìä SISTEMA UNIFICADO GLOBAL - Filtrado completado: ${visibleCount} visibles, ${hiddenCount} ocultos`);
        
        // MEGA FORZADO para productos API y Main despu√©s del filtrado
        setTimeout(() => {
