        <script>
        // Funci√≥n para manejar el filtrado desde el men√∫
        function handleNavFilter(gender, category) {
            // Ocultar todos los productos
            document.querySelectorAll('.product-card').forEach(product => {
                product.style.display = 'none';
            });
            
            // Mostrar productos correspondientes
            const targetProducts = document.querySelectorAll(`
                .product-card[data-gender="${gender}"][data-category="${category}"]
            `);
            
            targetProducts.forEach(product => {
                product.style.display = 'block';
                product.classList.add('filter-highlight');
            });
            
            // Desplazamiento suave
            const targetSection = document.getElementById('prendas-exclusivas');
            // targetSection.scrollIntoView(); // Eliminado para evitar scroll autom√°tico
            
            // Eliminar highlight despu√©s de 2 segundos
            setTimeout(() => {
                targetProducts.forEach(product => {
                    product.classList.remove('filter-highlight');
                });
            }, 2000);
        }
        
        // Event listeners para los items del men√∫
        document.querySelectorAll('.nav-filter').forEach(item => {
            item.addEventListener('click', function(e) {
                e.preventDefault();
                const gender = this.dataset.gender;
                const category = this.dataset.category;
                handleNavFilter(gender, category);
            });
        });
        
        // Funci√≥n de b√∫squeda optimizada mejorada - SIN LOOPS INFINITOS
        function searchProducts() {
            const searchInput = document.getElementById('searchInput');
            if (!searchInput) return;
            
            const term = searchInput.value.toLowerCase().trim();
            let hasResults = false;
            let totalProducts = 0;
            let matchedProducts = 0;
            
            console.log('üîç Buscando:', term || 'todos los productos');
            
            // Si no hay t√©rmino de b√∫squeda, mostrar todos los productos
            if (!term) {
                document.querySelectorAll('.product-card, .api-product, .main-product-card').forEach(product => {
                    product.style.display = 'block';
                    product.classList.remove('search-highlight');
                });
                return;
            }
            
            // Buscar en todos los productos (est√°ticos y din√°micos)
            const allProducts = document.querySelectorAll('.product-card, .api-product, .static-product, .main-product-card, [data-product-name]');
            
            allProducts.forEach(product => {
                totalProducts++;
                
                // Obtener texto de b√∫squeda del producto
                const productName = product.dataset.productName || 
                                   product.querySelector('.product-name, .card-title, h5, h6')?.textContent || '';
                const productDescription = product.querySelector('.product-description, .card-text, .description')?.textContent || '';
                const productCategory = product.dataset.category || 
                                       product.querySelector('.category, .badge')?.textContent || '';
                const productPrice = product.querySelector('.price, .precio')?.textContent || '';
                
                // Combinar todo el texto para b√∫squeda
                const searchText = `${productName} ${productDescription} ${productCategory} ${productPrice}`.toLowerCase();
                
                const isMatch = searchText.includes(term);
                
                if (isMatch) {
                    product.style.display = 'block';
                    product.classList.add('search-highlight');
                    matchedProducts++;
                    hasResults = true;
                } else {
                    product.style.display = 'none';
                    product.classList.remove('search-highlight');
                }
            });
            
            console.log(`üìä B√∫squeda completada: ${matchedProducts}/${totalProducts} productos encontrados`);
            
            // Mostrar resultado de la b√∫squeda
            if (!hasResults && term) {
                // Solo mostrar notificaci√≥n si hab√≠a un t√©rmino de b√∫squeda
                Swal.fire({
                    icon: 'info',
                    title: 'No se encontraron resultados',
                    text: `La b√∫squeda "${term}" no produjo resultados`,
                    toast: true,
                    position: 'top-end',
                    showConfirmButton: false,
                    timer: 3000
                });
            }
        }
        
        // B√∫squeda en tiempo real
        function setupRealTimeSearch() {
            const searchInput = document.getElementById('searchInput');
            const clearBtn = document.getElementById('clearSearchBtn');
            
            if (searchInput) {
                let searchTimeout;
                
                searchInput.addEventListener('input', function() {
                    const value = this.value.trim();
                    
                    // Mostrar/ocultar bot√≥n de limpiar
                    if (clearBtn) {
                        clearBtn.style.display = value ? 'block' : 'none';
                    }
                    
                    clearTimeout(searchTimeout);
                    searchTimeout = setTimeout(() => {
                        searchProducts();
                    }, 300); // Esperar 300ms despu√©s de que el usuario deje de escribir
                });
                
                // Tambi√©n b√∫squeda al presionar Enter
                searchInput.addEventListener('keypress', function(e) {
                    if (e.key === 'Enter') {
                        e.preventDefault();
                        searchProducts();
                    }
                });
            }
        }
        
        // Funci√≥n para limpiar b√∫squeda
        function clearSearch() {
            const searchInput = document.getElementById('searchInput');
            const clearBtn = document.getElementById('clearSearchBtn');
            
            if (searchInput) {
                searchInput.value = '';
                searchInput.focus();
            }
            
            if (clearBtn) {
                clearBtn.style.display = 'none';
            }
            
            // Mostrar todos los productos
            document.querySelectorAll('.product-card, .api-product, .static-product, [data-product-name]').forEach(product => {
                product.style.display = 'block';
                product.classList.remove('search-highlight');
            });
            
            console.log('üßπ B√∫squeda limpiada - todos los productos visibles');
        }
        </script>
        
