<script>
    document.addEventListener('DOMContentLoaded', function() {
        const PAYMENT_CONFIG = {
            API_KEY: 'TU_API_KEY_LIVE',
            CLIENT_ID: 'TU_CLIENT_ID',
            ENVIRONMENT: 'production'
        };
    
        // Manejar cambio de departamento
        document.getElementById('departamento').addEventListener('change', function() {
            const ciudadSelect = document.getElementById('ciudad');
            ciudadSelect.innerHTML = '<option value="">Selecciona una ciudad</option>';
            ciudadSelect.disabled = !this.value;
            
            if (this.value) {
                CONFIG.SHIPPING.departamentos[this.value].forEach(ciudad => {
                    const option = document.createElement('option');
                    option.value = ciudad;
                    option.textContent = ciudad;
                    ciudadSelect.appendChild(option);
                });
            }
        });
    
        // ValidaciÔøΩn de formulario
        function validateShippingForm() {
            let isValid = true;
            const form = document.forms[0];
            
            // Validar campos requeridos
            form.querySelectorAll(':required').forEach(input => {
                if (!input.checkValidity()) {
                    input.reportValidity();
                    isValid = false;
                }
            });
    
            // ValidaciÔøΩn adicional de ciudad
            const ciudad = document.getElementById('ciudad');
            if (ciudad.value === "") {
                ciudad.setCustomValidity('Selecciona una ciudad');
                ciudad.reportValidity();
                isValid = false;
            }
    
            return isValid;
        }
    
        // Generar ID de orden
        function generateOrderId() {
            return `ORD-${Date.now()}-${Math.floor(Math.random() * 1000)}`;
        }
    
        // Obtener datos del cliente
        function getCustomerData() {
            return {
                email: document.getElementById('emailCliente').value,
                name: document.getElementById('nombreCompleto').value,
                phone: document.getElementById('telefono').value,
                address: document.getElementById('direccion').value,
                city: document.getElementById('ciudad').value,
                zipCode: document.getElementById('codigoPostal').value
            };
        }
    
    // Configuraci√≥n de datos
    const SHIPPING_CONFIG = {
        departamentos: {
            "cundinamarca": ["Madrid", "ZipaquirÔøΩ", "ChÔøΩa", "Soacha", "Funza", "Mosquera", "FacatativÔøΩ"],
            "antioquia": ["MedellÔøΩn", "Bello", "Envigado", "ItagÔøΩÔøΩ", "Rionegro", "Sabaneta"],
            "bogota": ["BogotÔøΩ D.C."],
            "valle": ["Cali", "Palmira", "Buenaventura", "Yumbo", "JamundÔøΩ", "TuluÔøΩ", "Buga"]
        }
    };
    
    // Cargar SDK Bold
    function loadBoldSDK() {
        return new Promise((resolve, reject) => {
            if (typeof Bold !== 'undefined') return resolve();
            
            const script = document.createElement('script');
            script.src = 'https://checkout.bold.co/library/boldPaymentButton.js';
            script.onload = resolve;
            script.onerror = () => reject(new Error('Error cargando SDK Bold'));
            document.head.appendChild(script);
        });
    }
    
        // Inicializar checkout
        async function initializeBoldCheckout() {
            try {
                const customerData = getCustomerData();
                const cartTotal = parseFloat(document.querySelector('[data-total]').textContent.replace(/[^0-9.]/g, ''));
                
                const orderData = {
                    id: generateOrderId(),
                    amount: Math.round(cartTotal * 100), // Total ya incluye env√≠o
                    currency: 'COP',
                    customer: customerData
                };
    
                // Cargar SDK
                await loadBoldSDK();                // Inicializar Bold
                Bold.initialize({
                    key: CONFIG.BOLD.API_KEY,
                    orderId: orderData.id,
                    amount: orderData.amount,
                    currency: orderData.currency,
                    container: '#bold-checkout-container',
                    redirectionUrl: CONFIG.BOLD.REDIRECTION_URL,
                    customer: {
                        email: customerData.email,
                        name: customerData.name,
                        phone: customerData.phone,
                        address: {
                            address: customerData.address,
                            city: customerData.city,
                            zipCode: customerData.zipCode
                        }
                    },
                    onError: (error) => {
                        Swal.fire('Error en el pago', error.message, 'error');
                    }
                });
    
            } catch (error) {
                console.error('Error:', error);
                Swal.fire('Error', 'No se pudo cargar el sistema de pagos', 'error');
            }
        }
    
        // Manejar clic en Continuar
        document.getElementById('btnContinueToPayment').addEventListener('click', async function(e) {
            e.preventDefault();
            
            if (validateShippingForm()) {
                const shippingModal = bootstrap.Modal.getInstance(document.getElementById('ShippingModal'));
                shippingModal.hide();
    
                const paymentModal = new bootstrap.Modal(document.getElementById('PaymentModal'));
                paymentModal.show();
    
                try {
                    await initializeBoldCheckout();
                } catch (error) {
                    paymentModal.hide();
                    shippingModal.show();
                }
            }
        });
    
        // Manejar respuesta de pago
        const urlParams = new URLSearchParams(window.location.search);
        if (urlParams.has('bold-transaction-id')) {
            const transactionId = urlParams.get('bold-transaction-id');
            const status = urlParams.get('bold-tx-status');
            
            Swal.fire({
                icon: status === 'approved' ? 'success' : 'info',
                title: status === 'approved' ? 'ÔøΩPago Exitoso!' : 'Pago Pendiente',
                html: `ID TransacciÔøΩn: <b>${transactionId}</b><br>Estado: <b>${status}</b>`
            }).then(() => {
                localStorage.removeItem('cartItems');
                window.location.href = '/';
            });
        }
    });
    </script>
