    // Funci√≥n para corregir URLs con caracteres especiales
    window.fixImageUrls = function() {
        console.log('üîß Corrigiendo URLs de im√°genes con caracteres especiales...');
        
        // Obtener todas las im√°genes
        const images = document.querySelectorAll('img');
        let fixed = 0;
        
        images.forEach(img => {
            const originalSrc = img.src;
            
            // Lista de caracteres problem√°ticos y sus correcciones
            const fixes = [
                { from: '%EF%BF%BD', to: '' }, // Reemplazar caracteres corruptos
                { from: 'Pantal%EF%BF%BDn', to: 'Pantalon' },
                { from: 'Chaqueta%20Caf%EF%BF%BD', to: 'Chaqueta%20Cafe' },
                { from: 'Chaqueta%20Ros%EF%BF%BD', to: 'Chaqueta%20Rosa' },
                { from: '%C3%B1', to: 'n' }, // √±
                { from: '%C3%A9', to: 'e' }, // √©
                { from: '%C3%AD', to: 'i' }, // √≠
                { from: '%C3%B3', to: 'o' }, // √≥
                { from: '%C3%BA', to: 'u' }, // √∫
                { from: '%C3%A1', to: 'a' }, // √°
            ];
            
            let newSrc = originalSrc;
            let changed = false;
            
            // Aplicar todas las correcciones
            fixes.forEach(fix => {
                if (newSrc.includes(fix.from)) {
                    newSrc = newSrc.replace(new RegExp(fix.from, 'g'), fix.to);
                    changed = true;
                }
            });
            
            // Si se hizo alg√∫n cambio, actualizar la imagen
            if (changed) {
                console.log(`üîß Corrigiendo: ${originalSrc} -> ${newSrc}`);
                img.src = newSrc;
                fixed++;
                
                // Agregar un listener para el error y tratar con una imagen de respaldo
                img.addEventListener('error', function() {
                    console.log(`‚ùå Imagen a√∫n con error despu√©s de correcci√≥n: ${newSrc}`);
                    // Usar imagen de placeholder si existe
                    const placeholder = 'images/placeholder.jpg';
                    if (this.src !== placeholder) {
                        this.src = placeholder;
                    }
                }, { once: true });
            }
        });
        
        console.log(`‚úÖ ${fixed} URLs de im√°genes corregidas`);
    };

