    // Obtener datos del carrito actualizado
    function getCartData() {
        try {
            const cart = JSON.parse(localStorage.getItem('carrito') || '[]');
            const subtotal = cart.reduce((sum, item) => {
                const precio = parseFloat(item.precio || item.price) || 0;
                const cantidad = parseInt(item.cantidad || item.quantity) || 1;
                return sum + (precio * cantidad);
            }, 0);
            const shippingCost = 0; // ENV√çO SIEMPRE GRATIS
            
            return {
                total: parseFloat(document.querySelector('[data-total]').textContent.replace(/[^0-9.]/g, '')) || 0,
                items: cart,
                shippingCost: shippingCost
            };
        } catch (error) {
            console.error('Error obteniendo datos del carrito:', error);
            return { total: 0, items: [], shippingCost: 0 };
        }
