    // Inicializar checkout de Bold
    async function initializeBoldCheckout() {
        try {
            const cartData = getCartData();
            const customerData = getCustomerData();
            
            // Validar datos esenciales
            if (!customerData.email || !customerData.name) {
                throw new Error('InformaciÔøΩn del cliente incompleta');
            }

            // Generar orden
            const orderData = {
                id: generateOrderId(),
                amount: Math.round((cartData.total + cartData.shippingCost) * 100), // Incluir envÔøΩo
                currency: 'COP',
                customer: customerData
            };

            // Obtener firma del backend
            const response = await fetch('/api/generate-signature', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    orderId: orderData.id,
                    amount: orderData.amount,
                    currency: orderData.currency
                })
            });
            
            const { hash } = await response.json();

            // Cargar SDK Bold
            await loadBoldSDK();
            
            // Inicializar checkout
            Bold.initialize({
                key: BOLD_CONFIG.API_KEY,
                orderId: orderData.id,
                amount: orderData.amount,
                currency: orderData.currency,
                integritySignature: hash,
                redirectionUrl: BOLD_CONFIG.REDIRECTION_URL,
                renderMode: 'embedded',
                container: '#bold-checkout-container',
                customer: {
                    email: orderData.customer.email,
                    name: orderData.customer.name,
                    phone: orderData.customer.phone,
                    address: {
                        address: orderData.customer.address,
                        city: orderData.customer.city,
                        zipCode: orderData.customer.zipCode
                    }
                },
                onPaymentSuccess: (data) => {
                    console.log('Pago exitoso:', data);
                },
                onError: (error) => {
                    Swal.fire('Error en el pago', error.message, 'error');
                }
            });

        } catch (error) {
            console.error('Error inicializando Bold:', error);
            Swal.fire('Error', `Error al procesar el pago: ${error.message}`, 'error');
        }
    }

