                    ciudadSelect.innerHTML += `<option value="${ciudad}">${ciudad}</option>`;
                });
                ciudadSelect.disabled = false;
            }
        });

        // BotÔøΩn Continuar en ShippingModal
        document.getElementById('btnContinueToPayment').addEventListener('click', async function(e) {
            e.preventDefault();
            
            if (validateShippingForm()) {
                const shippingModal = bootstrap.Modal.getInstance(document.getElementById('ShippingModal'));
                shippingModal.hide();
                
                // Mostrar spinner de carga
                document.getElementById('bold-checkout-container').innerHTML = `
                    <div class="spinner-border text-primary" role="status">
                        <span class="visually-hidden">Cargando pasarela de pago...</span>
                    </div>`;

                // Inicializar Bold despuÔøΩs de mostrar el modal
                const paymentModal = new bootstrap.Modal(document.getElementById('PaymentModal'));
                paymentModal.show();
                
                try {
                    await initializeBoldCheckout();
                } catch (error) {
                    paymentModal.hide();
                    shippingModal.show();
                }
            }
        });

        // Manejar respuesta de pago
        const urlParams = new URLSearchParams(window.location.search);
        if (urlParams.has('bold-transaction-id')) {
            const transactionId = urlParams.get('bold-transaction-id');
            const status = urlParams.get('bold-tx-status');
            
            Swal.fire({
                icon: status === 'approved' ? 'success' : 'info',
                title: status === 'approved' ? 'ÔøΩPago Exitoso!' : 'Pago Pendiente',
                html: `ID TransacciÔøΩn: <b>${transactionId}</b><br>Estado: <b>${status}</b>`
            }).then(() => {
                localStorage.removeItem('cartItems');
                window.location.href = '/';
            });
        }
    });

    // Event listener cuando el PaymentModal se abre
    document.getElementById('PaymentModal').addEventListener('shown.bs.modal', () => {
        console.log('üî• PaymentModal abierto - inicializando MercadoPago...');
        setTimeout(() => {
            window.initMercadoPago();
        }, 500);
    });

    // Manejar cierre del modal de pago
    const paymentModal = document.getElementById('PaymentModal');
    if (paymentModal) {
        paymentModal.addEventListener('hidden.bs.modal', () => {
            const shippingModal = new bootstrap.Modal(document.getElementById('ShippingModal'));
            shippingModal.show();
        });
    }
</script>
