<script>
document.addEventListener('DOMContentLoaded', function() {
    // Inicializar todos los dropdowns
    const dropdowns = document.querySelectorAll('.dropdown-toggle');
    dropdowns.forEach(dropdown => {
        new bootstrap.Dropdown(dropdown, {
            hover: false // Desactivar hover en mÔøΩviles
        });
    });

    // Agregar efecto hover en desktop
    if (window.innerWidth >= 992) {
        document.querySelectorAll('.dropdown').forEach(dropdown => {
            dropdown.addEventListener('mouseenter', function() {
                const dropdownToggle = this.querySelector('.dropdown-toggle');
                const bsDropdown = bootstrap.Dropdown.getInstance(dropdownToggle);
                if (bsDropdown) {
                    bsDropdown.show();
                }
            });

            dropdown.addEventListener('mouseleave', function() {
                const dropdownToggle = this.querySelector('.dropdown-toggle');
                const bsDropdown = bootstrap.Dropdown.getInstance(dropdownToggle);
                if (bsDropdown) {
                    bsDropdown.hide();
                }
            });
        });
    }

    // Manejar clics en items del dropdown
    document.querySelectorAll('.dropdown-item').forEach(item => {
        item.addEventListener('click', function(e) {
            e.preventDefault();
            const filter = this.dataset.filter;
            // Aplicar filtro
            applyFilter(filter);
            // Cerrar dropdown en m√≥vil
            if (window.innerWidth < 992) {
                const dropdownMenu = this.closest('.dropdown-menu');
                const bsDropdown = bootstrap.Dropdown.getInstance(dropdownMenu.previousElementSibling);
                if (bsDropdown) {
                    bsDropdown.hide();
                }
            }
        });
    });

    // ‚úÖ FUNCI√ìN APPLYFILTER MEJORADA
    function applyFilter(filter) {
        console.log('üîß Aplicando filtro:', filter);
        
        try {
            // Normalizar el filtro
            const normalizedFilter = (!filter || filter === 'undefined' || filter === undefined) ? 'all' : filter;
            
            // Si existe una funci√≥n de filtrado espec√≠fica, usarla
            if (typeof window.filterProducts === 'function') {
                window.filterProducts(normalizedFilter, 'all');
                return;
            }
            
            // Filtrado b√°sico por categor√≠a
            const allProducts = document.querySelectorAll('.product-card, .api-product, .main-product-card');
            
            allProducts.forEach(product => {
                if (normalizedFilter === 'all' || normalizedFilter === 'todas') {
                    // Mostrar todos los productos
                    product.style.display = 'block';
                } else {
                    // Filtrar por categor√≠a, g√©nero o atributo
                    const category = product.dataset.category || '';
                    const gender = product.dataset.gender || '';
                    const productText = product.textContent.toLowerCase();
                    
                    const isMatch = category.toLowerCase().includes(normalizedFilter.toLowerCase()) ||
                                   gender.toLowerCase().includes(normalizedFilter.toLowerCase()) ||
                                   productText.includes(normalizedFilter.toLowerCase());
                    
                    product.style.display = isMatch ? 'block' : 'none';
                }
            });
            
            console.log('‚úÖ Filtro aplicado correctamente');
            
        } catch (error) {
            console.error('‚ùå Error aplicando filtro:', error);
            // En caso de error, mostrar todos
            const allProducts = document.querySelectorAll('.product-card, .api-product, .main-product-card');
            allProducts.forEach(product => {
                product.style.display = 'block';
            });
        }
    }
    
    // Hacer la funci√≥n global
    window.applyFilter = applyFilter;
});
</script>

