<script>
    function searchProducts() {
        const term = normalizeText(document.getElementById('searchInput').value);
        const products = document.querySelectorAll('.product-card');
        let firstMatch = null;
    
        products.forEach(product => {
            const name = normalizeText(product.querySelector('.card-title').textContent);
            const category = product.dataset.category;
            const gender = product.dataset.gender;
            const colors = Array.from(product.querySelectorAll('.color-btn')).map(btn => btn.dataset.color);
            const sizes = Array.from(product.querySelectorAll('.size-btn')).map(btn => btn.dataset.size);
    
            const match = name.includes(term) || 
                        category.includes(term) || 
                        colors.some(c => c.includes(term)) || 
                        sizes.some(s => s.includes(term));
    
            product.style.display = match ? 'block' : 'none';
            
            if(match && !firstMatch) {
                firstMatch = product;
                product.classList.add('search-highlight');
            } else {
                product.classList.remove('search-highlight');
            }
        });
    
        if(firstMatch) {
            // firstMatch.scrollIntoView({
            //     behavior: 'smooth',
            //     block: 'center'
            // }); // Deshabilitado para evitar scroll autom√°tico
            
            // Resaltado animado
            setTimeout(() => {
                firstMatch.classList.remove('search-highlight');
            }, 2000);
        }
    }
    
    function normalizeText(text) {
        return text.toLowerCase()
            .normalize("NFD")
            .replace(/[\u0300-\u036f]/g, "")
            .trim();
    }
    </script>
    
