        async getProductCount(categoryId) {
            try {
                // Usar el API de productos v2 para obtener estadÔøΩsticas por categorÔøΩa
                const response = await fetch(`api/productos-v2.php?category=${categoryId}`);
                const result = await response.json();
                
                if (result.success && result.data) {
                    return result.data.length;
                }
                
                // Si no funciona, intentar con el API de productos original
                const fallbackResponse = await fetch(`api/productos.php?category=${categoryId}`);
                const fallbackResult = await fallbackResponse.json();
                
                if (fallbackResult.success && fallbackResult.data) {
                    return fallbackResult.data.length;
                }
                
                return 0;
            } catch (error) {
                console.error(`Error al contar productos de ${categoryId}:`, error);
                // En lugar de nÔøΩmero aleatorio, devolver 0 para ser mÔøΩs preciso
                return 0;
