    // Funci√≥n para reproducir sonido de compra sintetizado
    function playPurchaseSound() {
        try {
            // Crear contexto de audio
            const audioContext = new (window.AudioContext || window.webkitAudioContext)();
            
            // Crear secuencia de tonos agradables para sonido de compra
            const notes = [
                { freq: 523.25, time: 0.0, duration: 0.15 },  // C5
                { freq: 659.25, time: 0.1, duration: 0.15 },  // E5
                { freq: 783.99, time: 0.2, duration: 0.25 }   // G5
            ];
            
            notes.forEach(note => {
                setTimeout(() => {
                    // Crear oscilador
                    const oscillator = audioContext.createOscillator();
                    const gainNode = audioContext.createGain();
                    
                    // Configurar oscilador
                    oscillator.type = 'sine';
                    oscillator.frequency.setValueAtTime(note.freq, audioContext.currentTime);
                    
                    // Configurar ganancia (volumen)
                    gainNode.gain.setValueAtTime(0, audioContext.currentTime);
                    gainNode.gain.linearRampToValueAtTime(0.1, audioContext.currentTime + 0.01);
                    gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + note.duration);
                    
                    // Conectar nodos
                    oscillator.connect(gainNode);
                    gainNode.connect(audioContext.destination);
                    
                    // Reproducir
                    oscillator.start(audioContext.currentTime);
                    oscillator.stop(audioContext.currentTime + note.duration);
                }, note.time * 1000);
            });
            
            console.log('üîä Reproduciendo sonido de compra');
        } catch (error) {
            console.warn('‚ö†Ô∏è No se pudo reproducir el sonido:', error);
        }
    }

    // Funci√≥n alternativa con sonido m√°s elaborado tipo "cha-ching"
    function playPurchaseSoundElaborated() {
        try {
            const audioContext = new (window.AudioContext || window.webkitAudioContext)();
            
            // Sonido de "cha-ching" m√°s realista
            const createChaChing = () => {
                // "Cha" - sonido de campana
                setTimeout(() => {
                    const oscillator1 = audioContext.createOscillator();
                    const gainNode1 = audioContext.createGain();
                    
                    oscillator1.type = 'triangle';
                    oscillator1.frequency.setValueAtTime(800, audioContext.currentTime);
                    oscillator1.frequency.exponentialRampToValueAtTime(1200, audioContext.currentTime + 0.1);
                    
                    gainNode1.gain.setValueAtTime(0, audioContext.currentTime);
                    gainNode1.gain.linearRampToValueAtTime(0.2, audioContext.currentTime + 0.01);
                    gainNode1.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.2);
                    
                    oscillator1.connect(gainNode1);
                    gainNode1.connect(audioContext.destination);
                    oscillator1.start(audioContext.currentTime);
                    oscillator1.stop(audioContext.currentTime + 0.2);
                }, 0);
                
                // "Ching" - sonido m√°s agudo
                setTimeout(() => {
                    const oscillator2 = audioContext.createOscillator();
                    const gainNode2 = audioContext.createGain();
                    
                    oscillator2.type = 'sine';
                    oscillator2.frequency.setValueAtTime(1500, audioContext.currentTime);
                    oscillator2.frequency.exponentialRampToValueAtTime(2000, audioContext.currentTime + 0.15);
                    
                    gainNode2.gain.setValueAtTime(0, audioContext.currentTime);
                    gainNode2.gain.linearRampToValueAtTime(0.15, audioContext.currentTime + 0.01);
                    gainNode2.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.3);
                    
                    oscillator2.connect(gainNode2);
                    gainNode2.connect(audioContext.destination);
                    oscillator2.start(audioContext.currentTime);
                    oscillator2.stop(audioContext.currentTime + 0.3);
                }, 150);
            };
            
            createChaChing();
            console.log('üîä Reproduciendo sonido de compra elaborado');
        } catch (error) {
            console.warn('‚ö†Ô∏è No se pudo reproducir el sonido elaborado:', error);
        }
    }

