        cargarEventos() {
            document.addEventListener('click', (e) => {
                if (e.target.closest('.add-to-cart-btn')) {
                    const btn = e.target.closest('.add-to-cart-btn');
                    const card = btn.closest('.product-card');
                    const selectedSize = card.querySelector('.size-btn.active');
                    const selectedColor = card.querySelector('.color-btn.active');
                    const productImage = card.querySelector('.product-image');
    
                    if (!selectedSize || !selectedColor) {
                        Swal.fire({
                            icon: 'error',
                            title: 'SelecciÔøΩn requerida',
                            text: 'Debes seleccionar talla y color',
                            toast: true,
                            position: 'top-end',
                            showConfirmButton: false,
                            timer: 3000
                        });
                        return;
                    }
    
                    const productData = {
                        id: btn.dataset.id,
                        nombre: btn.dataset.name,
                        precio: parseFloat(btn.dataset.price),
                        imagen: productImage.src,
                        talla: selectedSize.dataset.size,
                        color: selectedColor.dataset.color,
                        cantidad: 1
                    };
    
                    this.agregarProducto(productData);
                }
    
                if (e.target.closest('.remove-item')) {
                    const item = e.target.closest('.accordion-item');
                    this.eliminarItem(
                        item.dataset.id, 
                        item.dataset.talla, 
                        item.dataset.color
                    );
                }
    
                if (e.target.closest('.minus-btn, .plus-btn')) {
                    const input = e.target.closest('.quantity-selector').querySelector('input');
                    const item = e.target.closest('.accordion-item');
                    const nuevaCantidad = e.target.classList.contains('minus-btn') ? 
                        Math.max(1, parseInt(input.value) - 1) : 
                        parseInt(input.value) + 1;
                    
                    this.actualizarCantidad(
                        item.dataset.id,
                        item.dataset.talla,
                        item.dataset.color,
                        nuevaCantidad
                    );
                }
    
                if (e.target.id === 'clearCart') {
                    Swal.fire({
                        title: '¬øVaciar carrito?',
                        text: "¬°No podr√°s revertir esto!",
                        icon: 'warning',
                        showCancelButton: true,
                        confirmButtonColor: '#3085d6',
                        cancelButtonColor: '#d33',
                        confirmButtonText: 'S√≠, vaciar!'
                    }).then((result) => {
                        if (result.isConfirmed) this.vaciarCarrito();
                    });
                }
            });
        }
    
        agregarProducto(producto) {
            const stockDisponible = 10; // Obtener de tu base de datos
            const itemExistente = this.items.find(item => 
                item.id === producto.id && 
                item.talla === producto.talla && 
                item.color === producto.color
            );

    if (itemExistente) {
        if (itemExistente.cantidad >= stockDisponible) {
            Swal.fire('Stock agotado', 'No hay m√°s unidades disponibles', 'warning');
            return;
        }
        itemExistente.cantidad++;
    } else {
        this.items.push({...producto, cantidad: 1});
    }
    
            this.guardarLocalStorage();
            this.actualizarUI();
            this.mostrarNotificacion(producto.nombre);
            this.mostrarModalCarrito();
        }
    
        eliminarItem(id, talla, color) {
            this.items = this.items.filter(item => 
                !(item.id === id && 
                item.talla === talla && 
                item.color === color)
            );
            this.guardarLocalStorage();
            this.actualizarUI();
        }
    
        actualizarCantidad(id, talla, color, cantidad) {
            const item = this.items.find(item => 
                item.id === id && 
                item.talla === talla && 
                item.color === color
            );
            
            if (item) {
                item.cantidad = cantidad;
                this.guardarLocalStorage();
                this.actualizarUI();
            }
        }
    
        vaciarCarrito() {
            this.items = [];
            this.guardarLocalStorage();
            this.actualizarUI();
            Swal.fire('¬°Vac√≠o!', 'Tu carrito ha sido vaciado.', 'success');
        }
    
        guardarLocalStorage() {
            localStorage.setItem('carrito', JSON.stringify(this.items));
