    // Funci√≥n auxiliar para extraer tallas del DOM
    function extractSizesFromDOM(sizeElements) {
        const sizes = [];
        sizeElements.forEach(el => {
            let size = el.dataset.size || el.textContent.trim();
            
            // Filtrar tallas v√°lidas
            if (size) {
                // Excluir n√∫meros peque√±os que probablemente son contadores o badges
                if (/^\d+$/.test(size)) {
                    const num = parseInt(size);
                    // Solo incluir n√∫meros que son tallas t√≠picas (20-60)
                    if (num >= 20 && num <= 60) {
                        if (!sizes.includes(size)) sizes.push(size);
                    }
                } else if (/^(XS|S|M|L|XL|XXL|XXXL)$/i.test(size)) {
                    // Incluir tallas alfab√©ticas est√°ndar
                    if (!sizes.includes(size)) sizes.push(size);
                }
            }
        });
        
        console.log('üëï Tallas extra√≠das del DOM:', sizes);
        return sizes;
    }
    
    // Funci√≥n auxiliar para extraer colores del DOM
    function extractColorsFromDOM(colorElements) {
        const colors = [];
        colorElements.forEach(el => {
            const color = el.dataset.color || el.title || el.alt;
            if (color && !colors.includes(color)) {
                colors.push(color);
            }
        });
        return colors;
    }
    
    // Funci√≥n auxiliar para extraer im√°genes
    function extractImagesFromProduct(imgElement, fullData) {
        let images = {};
        
        // Primero intentar obtener del dataset
        if (imgElement?.dataset.images) {
            try {
                images = JSON.parse(imgElement.dataset.images);
            } catch (e) {
                console.warn('Error parsing images dataset:', e);
            }
        }
        
        // Si no hay im√°genes en dataset, usar datos de la API
        if (Object.keys(images).length === 0 && fullData) {
            if (fullData.gallery && fullData.gallery.length > 0) {
                fullData.gallery.forEach((img, index) => {
                    images[`Imagen ${index + 1}`] = img.startsWith('uploads/') ? img : `uploads/${img}`;
                });
            } else if (fullData.colors && fullData.colors.length > 0) {
                fullData.colors.forEach(color => {
                    const imgPath = fullData.main_image || imgElement?.src || '';
                    images[color] = imgPath.startsWith('uploads/') ? imgPath : `uploads/${imgPath}`;
                });
            } else if (fullData.main_image) {
                images['Principal'] = fullData.main_image.startsWith('uploads/') ? fullData.main_image : `uploads/${fullData.main_image}`;
            }
        }
        
        // Fallback: usar imagen actual
        if (Object.keys(images).length === 0 && imgElement?.src) {
            images['Principal'] = imgElement.src;
        }
        
        return images;
    }
    
