    // Funci√≥n para generar descripci√≥n desde datos del producto
    function generateDescriptionFromProductData(productData) {
        if (!productData) return 'Producto de alta calidad disponible en nuestra tienda.';
        
        let description = '';
        
        // Informaci√≥n del producto
        if (productData.name) {
            description += `${productData.name} es una prenda `;
        } else {
            description += 'Esta prenda es ';
        }
        
        // Categor√≠a espec√≠fica
        if (productData.category) {
            const category = productData.category.toLowerCase();
            if (category.includes('pantalon')) {
                description += 'confeccionada en tela de alta calidad, perfecta para el uso diario. ';
            } else if (category.includes('camisa')) {
                description += 'elegante y vers√°til, ideal para cualquier ocasi√≥n formal o casual. ';
            } else if (category.includes('chaqueta')) {
                description += 'dise√±ada para brindar estilo y comodidad en cualquier √©poca del a√±o. ';
            } else {
                description += 'de excelente calidad y dise√±o moderno. ';
            }
        } else {
            description += 'de alta calidad con excelente acabado y dise√±o moderno. ';
        }
        
        // G√©nero
        if (productData.gender) {
            const gender = productData.gender.toLowerCase();
            if (gender === 'hombre' || gender === 'male' || gender === 'm') {
                description += 'Dise√±ado especialmente para hombres que buscan estilo y comodidad. ';
            } else if (gender === 'mujer' || gender === 'female' || gender === 'f') {
                description += 'Creado pensando en la elegancia y sofisticaci√≥n femenina. ';
            } else {
                description += 'Perfecto para cualquier persona que aprecia la calidad. ';
            }
        }
        
        // Tallas disponibles
        if (productData.sizes && productData.sizes.length > 0) {
            const validSizes = productData.sizes.filter(size => 
                size && size.trim() && !size.includes('comprados') && size.length <= 4
            );
            if (validSizes.length > 0) {
                description += `Disponible en tallas: ${validSizes.join(', ')}. `;
            }
        }
        
        // Colores disponibles
        if (productData.colors && productData.colors.length > 0) {
            const colorNames = productData.colors.map(color => {
                if (typeof color === 'object' && color.name) {
                    return color.name;
                } else if (typeof color === 'string') {
                    return color;
                }
                return null;
            }).filter(Boolean);
            
            if (colorNames.length > 0) {
                description += `Disponible en los siguientes colores: ${colorNames.join(', ')}. `;
            }
        }
        
        // Informaci√≥n adicional espec√≠fica
        description += 'Fabricado con materiales premium que garantizan durabilidad y comodidad. ';
        description += 'Su dise√±o contempor√°neo lo convierte en la elecci√≥n perfecta para quienes valoran la calidad y el estilo.';
        
        return description;
    }

    // Funci√≥n para formatear categor√≠a
    function formatCategory(category) {
        if (!category) return 'General';
        return category.charAt(0).toUpperCase() + category.slice(1).toLowerCase();
    }

    // Funci√≥n para formatear g√©nero
    function formatGender(gender) {
        if (!gender) return 'Unisex';
        const genderMap = {
            'M': 'Hombres',
            'F': 'Mujeres',
            'U': 'Unisex',
            'male': 'Hombres',
            'female': 'Mujeres',
            'unisex': 'Unisex'
        };
        return genderMap[gender] || gender;
    }

