    // NUEVA FUNCI√ìN: Generar selector de tallas exacto como en index
    function generateSizeSelectorExact(sizes) {
        const sizeContainer = document.getElementById('productViewSizes');
        sizeContainer.innerHTML = '';
        
        if (!sizes || sizes.length === 0) {
            sizeContainer.innerHTML = '<span class="text-muted">Talla √∫nica</span>';
            return;
        }
        
        // Filtrar tallas v√°lidas - excluir n√∫meros sueltos que no son tallas
        const validSizes = sizes.filter(size => {
            const sizeStr = String(size).trim();
            
            // Excluir n√∫meros sueltos menores a 20 (probablemente no son tallas)
            if (/^\d+$/.test(sizeStr)) {
                const num = parseInt(sizeStr);
                if (num < 20 && num > 50) return false; // Excluir n√∫meros como 9, 10, etc.
            }
            
            // Incluir tallas v√°lidas como: S, M, L, XL, 28, 30, 32, etc.
            return /^(XS|S|M|L|XL|XXL|XXXL|\d{2,3})$/i.test(sizeStr) || 
                   /^(Extra Small|Small|Medium|Large|Extra Large)$/i.test(sizeStr);
        });
        
        console.log('üëï Tallas filtradas:', { original: sizes, filtered: validSizes });
        
        if (validSizes.length === 0) {
            sizeContainer.innerHTML = '<span class="text-muted">Talla √∫nica</span>';
            return;
        }
        
        validSizes.forEach((size, index) => {
            const sizeBtn = document.createElement('button');
            sizeBtn.type = 'button';
            sizeBtn.className = 'btn btn-sm btn-outline-dark size-btn';
            sizeBtn.dataset.size = size;
            sizeBtn.textContent = size;
            
            // Primer elemento activo por defecto
            if (index === 0) {
                sizeBtn.classList.add('active');
                sizeBtn.classList.remove('btn-outline-dark');
                sizeBtn.classList.add('btn-dark');
            }
            
            sizeBtn.addEventListener('click', function() {
                // Remover activo de otros
                sizeContainer.querySelectorAll('.size-btn').forEach(btn => {
                    btn.classList.remove('active', 'btn-dark');
                    btn.classList.add('btn-outline-dark');
                });
                
                // Activar este
                this.classList.add('active', 'btn-dark');
                this.classList.remove('btn-outline-dark');
                
                console.log('üëï Talla seleccionada en modal:', size);
            });
            
            sizeContainer.appendChild(sizeBtn);
        });
    }
    
