    // NUEVA FUNCI√ìN: Agregar al carrito desde modal con datos correctos
    function addToCartFromModalExact(data, selectedColor, selectedSize) {
        try {
            console.log('üõí [MODAL] === INICIO FUNCI√ìN addToCartFromModalExact ===');
            console.log('üõí [MODAL] Flag global activo:', window.isModalAddToCartActive);
            
            // VERIFICACI√ìN ADICIONAL: Solo ejecutar si el flag est√° activo
            if (!window.isModalAddToCartActive) {
                console.log('üö´ [MODAL] Flag no activo, cancelando operaci√≥n para evitar duplicaci√≥n');
                return;
            }
            
            console.log('üõí [MODAL] Agregando producto correcto al carrito:', {
                name: data.title,
                id: data.id,
                fullDataId: data.fullData?.id,
                selectedColor,
                selectedSize,
                fullData: !!data.fullData
            });
            
            let cart = JSON.parse(localStorage.getItem('carrito') || '[]');
            console.log('üõí [MODAL] Estado del carrito ANTES de agregar:', cart.length, 'items');
            
            // Usar el ID correcto de la base de datos
            const correctId = data.fullData?.id || data.id || `modal_${Date.now()}`;
            const correctName = data.fullData?.name || data.title;
            
            const uniqueKey = `${correctId}-${selectedSize || 'default'}-${selectedColor || 'default'}`;
            
            const existingItemIndex = cart.findIndex(item => 
                item.id === correctId && 
                item.size === selectedSize && 
                item.color === selectedColor
            );
            
            if (existingItemIndex > -1) {
                cart[existingItemIndex].quantity = (cart[existingItemIndex].quantity || 1) + 1;
                console.log('‚ûï [MODAL] Incrementando cantidad existente');
            } else {
                // Calcular precio correcto del modal usando datos de BD
                let finalPrice = 0;
                
                // PRIORIDAD 1: Datos de la base de datos (sale_price primero)
                if (data.fullData?.sale_price) {
                    finalPrice = parseFloat(data.fullData.sale_price) || 0;
                    console.log('üí∞ [MODAL] Usando precio con descuento de BD:', finalPrice);
                } else if (data.fullData?.price) {
                    finalPrice = parseFloat(data.fullData.price) || 0;
                    console.log('üí∞ [MODAL] Usando precio regular de BD:', finalPrice);
                } else {
                    // FALLBACK: Extraer precio desde el texto del modal (precio actual, no el tachado)
                    const priceText = data.price || '';
                    finalPrice = parseFloat(priceText.replace(/[^0-9]/g, '')) || 0;
                    console.log('üí∞ [MODAL] Usando precio extra√≠do del modal:', finalPrice, 'desde:', priceText);
                }
                
                const cartItem = {
                    id: correctId, // Usar ID correcto de la BD
                    name: correctName, // Usar nombre correcto de la BD
                    price: finalPrice, // Usar precio calculado correctamente
                    image: data.image,
                    size: selectedSize || 'Talla √∫nica',
                    color: selectedColor || 'Color √∫nico',
                    quantity: 1,
                    timestamp: Date.now(),
                    uniqueKey: uniqueKey,
                    source: 'modal',
                    // Datos adicionales para referencia
                    category: data.fullData?.category_id || data.category,
                    gender: data.fullData?.gender || data.gender,
                    productData: data.fullData // Guardar datos completos por si acaso
                };
                cart.push(cartItem);
                console.log('‚úÖ [MODAL] Nuevo producto agregado correctamente:', cartItem);
            }
            
            localStorage.setItem('carrito', JSON.stringify(cart));
            updateCartCounterExact(cart);
            
            // Actualizar badge del carrito flotante
            updateFloatingCartBadge(cart);
            
            console.log('üéØ [MODAL] Producto agregado exitosamente al carrito');
            console.log('üõí [MODAL] Estado del carrito DESPU√âS de agregar:', cart.length, 'items');
            
            // üéØ CERRAR AUTOM√ÅTICAMENTE EL MODAL DESPU√âS DE AGREGAR AL CARRITO
            setTimeout(() => {
                const productModal = document.getElementById('ProductViewModal');
                if (productModal) {
                    try {
                        // Intentar con Bootstrap primero
                        if (typeof bootstrap !== 'undefined' && bootstrap.Modal && bootstrap.Modal.getInstance) {
                            const modalInstance = bootstrap.Modal.getInstance(productModal);
                            if (modalInstance) {
                                modalInstance.hide();
                                console.log('‚ú® [MODAL] Modal cerrado autom√°ticamente con Bootstrap despu√©s de agregar al carrito');
                                return;
                            }
                        }
                        
                        // Fallback manual m√°s robusto
                        console.log('üîÑ [MODAL] Usando fallback manual para cerrar modal');
                        productModal.classList.remove('show');
                        productModal.style.display = 'none';
                        productModal.setAttribute('aria-hidden', 'true');
                        document.body.classList.remove('modal-open');
                        
                        // Remover backdrop
                        const backdrops = document.querySelectorAll('.modal-backdrop');
                        backdrops.forEach(backdrop => backdrop.remove());
                        
                        // Restaurar scroll del body
                        document.body.style.overflow = '';
                        document.body.style.paddingRight = '';
                        
                        console.log('‚ú® [MODAL] Modal cerrado con fallback robusto despu√©s de agregar al carrito');
                        
                    } catch (error) {
                        console.error('‚ùå [MODAL] Error al cerrar modal autom√°ticamente:', error);
                        // Fallback de emergencia
                        productModal.style.display = 'none';
                        document.body.classList.remove('modal-open');
                    }
                }
            }, 500); // Peque√±o delay para que el usuario vea la confirmaci√≥n
            
        } catch (error) {
            console.error('‚ùå [MODAL] Error agregando al carrito:', error);
            alert('Error agregando el producto al carrito. Por favor intenta de nuevo.');
        } finally {
            // SIEMPRE limpiar AMBOS flags al final, sin importar si hubo error o no
            setTimeout(() => {
                window.isModalAddToCartActive = false;
                window.isModalAddToCartInProgress = false;
                console.log('üßπ [MODAL] Ambos flags limpiados exitosamente');
            }, 100);
        }
    }
    
    // NUEVA FUNCI√ìN: Actualizar badge del carrito flotante
    function updateFloatingCartBadge(cart) {
        const totalItems = cart.reduce((total, item) => total + (parseInt(item.quantity) || 1), 0);
        
        // Actualizar badge del carrito flotante
        const floatingBadge = document.querySelector('.float-cart .cart-badge');
        if (floatingBadge) {
            floatingBadge.textContent = totalItems;
            floatingBadge.style.display = totalItems > 0 ? 'flex' : 'none';
        }
        
        // Actualizar otros badges tambi√©n
        const badges = document.querySelectorAll('.cart-badge, .badge[data-cart-count], #cart-count');
        badges.forEach(badge => {
            badge.textContent = totalItems;
            badge.style.display = totalItems > 0 ? 'inline-block' : 'none';
        });
    }
    
    
