<!-- Script para manejo de formulario -->
<script>
document.addEventListener('DOMContentLoaded', function() {
    // Cargar departamentos
    const departamentoSelect = document.getElementById('departamento');
    const ciudadSelect = document.getElementById('ciudad');

    // Poblar select de departamentos
    Object.keys(COLOMBIA_CONFIG.departamentos).sort().forEach(depKey => {
        const option = document.createElement('option');
        option.value = depKey;
        option.textContent = depKey.charAt(0).toUpperCase() + 
                           depKey.slice(1).replace('_', ' ');
        departamentoSelect.appendChild(option);
    });

    // Manejar cambio de departamento
    departamentoSelect.addEventListener('change', function() {
        ciudadSelect.innerHTML = '<option value="">Selecciona una ciudad</option>';
        ciudadSelect.disabled = !this.value;
        
        if (this.value) {
            COLOMBIA_CONFIG.departamentos[this.value]
                .sort()
                .forEach(ciudad => {
                    const option = document.createElement('option');
                    option.value = ciudad;
                    option.textContent = ciudad;
                    ciudadSelect.appendChild(option);
                });
        }

        // ValidaciÔøΩn personalizada
        if (this.value) {
            this.classList.remove('is-invalid');
            this.classList.add('is-valid');
        } else {
            this.classList.remove('is-valid');
            this.classList.add('is-invalid');
        }
    });

    // Validar selecciÔøΩn de ciudad
    ciudadSelect.addEventListener('change', function() {
        if (this.value) {
            this.classList.remove('is-invalid');
            this.classList.add('is-valid');
        } else {
            this.classList.remove('is-valid');
            this.classList.add('is-invalid');
        }
    });

    // ValidaciÔøΩn del formulario
    const form = document.querySelector('.needs-validation');
    form.addEventListener('submit', function(event) {
        if (!form.checkValidity()) {
            event.preventDefault();
            event.stopPropagation();
        }

        // Validar departamento y ciudad
        if (!departamentoSelect.value) {
            departamentoSelect.classList.add('is-invalid');
            event.preventDefault();
        }
        if (!ciudadSelect.value) {
            ciudadSelect.classList.add('is-invalid');
            event.preventDefault();
        }

        form.classList.add('was-validated');
    }, false);
});
</script>

