<script>
// Filtrado Mejorado con G√©nero
document.addEventListener("DOMContentLoaded", function() {
    const genderButtons = document.querySelectorAll('.btn-gender');
    const categoryButtons = document.querySelectorAll('.btn-category');
    
    // Manejar clics en g√©neros
    genderButtons.forEach(btn => {
        btn.addEventListener('click', function() {
            const selectedGender = this.dataset.gender;
            
            // Actualizar UI g√©neros
            genderButtons.forEach(b => b.classList.remove('active'));
            this.classList.add('active');
            
            // Mostrar categor√≠as correspondientes
            document.querySelector('.categoria-hombre').classList.toggle('d-none', selectedGender === 'mujer');
            document.querySelector('.categoria-mujer').classList.toggle('d-none', selectedGender === 'hombre');
            
            // Resetear categor√≠as
            categoryButtons.forEach(b => b.classList.remove('active'));
            document.querySelector('[data-filter="all"]').classList.add('active');
            
            // Filtrar productos
            window.filterProducts(selectedGender, 'all');
        });
    });

    // Manejar clics en categor√≠as
    categoryButtons.forEach(btn => {
        btn.addEventListener('click', function() {
            const activeGender = document.querySelector('.btn-gender.active').dataset.gender;
            const filter = this.dataset.filter;
            
            // Actualizar UI categor√≠as
            categoryButtons.forEach(b => b.classList.remove('active'));
            this.classList.add('active');
            
            // Filtrar productos
            window.filterProducts(activeGender, filter);
        });
    });

    // La funci√≥n filterProducts ya est√° definida globalmente arriba (l√≠nea 250)
    
    // Test inicial del sistema de filtrado
    setTimeout(() => {
        console.log('üß™ Test del sistema de filtrado:');
        console.log(`   - Botones de g√©nero encontrados: ${genderButtons.length}`);
        console.log(`   - Botones de categor√≠a encontrados: ${categoryButtons.length}`);
        console.log(`   - Funci√≥n global filterProducts disponible: ${typeof window.filterProducts}`);
        
        // Test de filtrado inicial
        const allProducts = document.querySelectorAll('.product-card, .api-product, .main-product-card');
        console.log(`   - Productos totales encontrados: ${allProducts.length}`);
        
        allProducts.forEach((product, index) => {
            console.log(`   - Producto ${index + 1}: cat="${product.dataset.category}", gender="${product.dataset.gender}"`);
        });
    }, 2000);

});
