    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const track = document.querySelector('.quantum-track');
            const slides = document.querySelectorAll('.hologram-card');
            
            // Clonar slides para efecto infinito
            const cloneSlides = () => {
                const clones = [];
                slides.forEach(slide => {
                    const clone = slide.cloneNode(true);
                    clone.setAttribute('aria-hidden', 'true');
                    clones.push(clone);
                });
                return clones;
            }

            // Insertar clones al final
            track.append(...cloneSlides());

            // Observador para reinicio suave
            const observer = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if(entry.isIntersecting) {
                        const firstSlide = track.firstElementChild;
                        if(firstSlide.getBoundingClientRect().right < 0) {
                            track.style.transition = 'none';
                            track.appendChild(firstSlide.cloneNode(true));
                            firstSlide.remove();
                            void track.offsetWidth;
                        }
                    }
                });
            }, { threshold: 0.1 });

            observer.observe(track);

            // OptimizaciÔøΩn para resize
            let resizeTimer;
            window.addEventListener('resize', () => {
                clearTimeout(resizeTimer);
                resizeTimer = setTimeout(() => {
                    track.style.animation = 'none';
                    void track.offsetWidth;
                    track.style.animation = 'quantumFlow 30s linear infinite';
                }, 100);
            });
        });
    </script>
