<script>
document.addEventListener('DOMContentLoaded', () => {
    const video = document.getElementById('videoTour');
    const modal = document.getElementById('videoModal');
    
    // Control de video
    const videoControls = {
        play: () => video[video.paused ? 'play' : 'pause'](),
        mute: () => video.muted = !video.muted,
        updateUI: () => {
            document.querySelectorAll('.video-control').forEach(btn => {
                const action = btn.dataset.action;
                btn.innerHTML = action === 'play' 
                    ? `<i class="fas fa-${video.paused ? 'play' : 'pause'} me-2"></i>${video.paused ? 'Reproducir' : 'Pausar'}`
                    : `<i class="fas fa-volume-${video.muted ? 'mute' : 'up'} me-2"></i>${video.muted ? 'Activar' : 'Silenciar'}`;
            });
        }
    };

    // Eventos del modal
    modal.addEventListener('shown.bs.modal', () => {
        video.play().catch(error => console.error('Error de autoplay:', error));
        videoControls.updateUI();
    });
    
    modal.addEventListener('hidden.bs.modal', () => video.pause());

    // Controladores de botones
    document.querySelectorAll('.video-control').forEach(btn => {
        btn.addEventListener('click', () => {
            videoControls[btn.dataset.action]();
            videoControls.updateUI();
        });
    });
});
</script>

