    // FunciÔøΩn para cargar productos en el dashboard
    function loadDashboardProducts() {
        console.log('?? Cargando productos para el dashboard...');
        
        try {
            // Obtener el contenedor del dashboard
            const dashboardContainer = document.getElementById('dashboardProducts');
            if (!dashboardContainer) {
                console.warn('?? Contenedor de productos del dashboard no encontrado');
