            for (let i = 0; i < Math.min(allProducts.length, maxProducts); i++) {
                const product = allProducts[i];
                
                // Extraer datos del producto
                const name = product.querySelector('.card-title')?.textContent || 'Producto sin nombre';
                const price = product.querySelector('.h4')?.textContent || '$0';
                const image = product.querySelector('.product-image')?.src || 'images/default-product.jpg';
                const category = product.getAttribute('data-category') || 'Sin categorÔøΩa';
                const gender = product.getAttribute('data-gender') || 'Unisex';
                const productId = product.querySelector('.add-to-cart-btn')?.getAttribute('data-id') || 'product-' + i;
                
                productsHTML += `
                    <div class="col-md-6 col-lg-4 mb-3">
                        <div class="card h-100 shadow-sm dashboard-product-card">
                            <img src="${image}" class="card-img-top" alt="${name}">
                            <div class="card-body p-3">
                                <h6 class="card-title mb-2">${name}</h6>
                                <p class="card-text small text-muted mb-2">
                                    <span class="badge bg-secondary">${category}</span>
                                    <span class="badge bg-info">${gender}</span>
                                </p>
                                <div class="d-flex justify-content-between align-items-center">
                                    <strong class="text-primary">${price}</strong>
                                    <div class="btn-group" role="group">
                                        <button class="btn btn-sm btn-outline-primary" onclick="viewProduct('${productId}')" title="Ver producto">
                                            <i class="fas fa-eye"></i>
                                        </button>
                                        <button class="btn btn-sm btn-outline-success" onclick="editProduct('${productId}')" title="Editar producto">
                                            <i class="fas fa-edit"></i>
                                        </button>
                                        <button class="btn btn-sm btn-outline-danger" onclick="deleteProduct('${productId}')" title="Eliminar producto">
                                            <i class="fas fa-trash"></i>
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                `;
            }
            
            // Si no hay productos, mostrar mensaje
            if (allProducts.length === 0) {
                productsHTML = `
                    <div class="col-12 text-center py-5">
                        <i class="fas fa-box-open fa-3x text-muted mb-3"></i>
                        <h5 class="text-muted">No hay productos disponibles</h5>
                        <p class="text-muted">Agrega productos a tu tienda para verlos aquÔøΩ</p>
                    </div>
                `;
            }
            
            // Insertar HTML en el contenedor
            dashboardContainer.innerHTML = productsHTML;
            
            console.log('? Productos cargados en el dashboard correctamente');
            
        } catch (error) {
            console.error('? Error al cargar productos del dashboard:', error);
            
            // Mostrar mensaje de error en el dashboard
            const dashboardContainer = document.getElementById('dashboardProducts');
            if (dashboardContainer) {
                dashboardContainer.innerHTML = `
                    <div class="col-12 text-center py-5">
                        <i class="fas fa-exclamation-triangle fa-3x text-warning mb-3"></i>
                        <h5 class="text-warning">Error al cargar productos</h5>
                        <p class="text-muted">Hubo un problema al cargar los productos: ${error.message}</p>
                    </div>
                `;
            }
        }
    }
    
