    // Inicializar cuando el DOM estÔøΩ listo
    document.addEventListener('DOMContentLoaded', function() {
        console.log('?? Inicializando sistema de administraciÔøΩn...');
        
        // Configurar funcionalidad de bÔøΩsqueda
        const searchInput = document.getElementById('searchInput');
        const searchBtn = document.querySelector('.btn-search');
        
        function performSearch() {
            const searchTerm = searchInput.value.trim();
            if (searchTerm) {
                console.log('?? BÔøΩsqueda:', searchTerm);
                filterProductsBySearch(searchTerm);
            }
        }
        
        function filterProductsBySearch(searchTerm) {
            const productCards = document.querySelectorAll('.product-card, .product-item');
            let visibleCount = 0;
            
            productCards.forEach(card => {
                const productName = card.querySelector('h5, .card-title')?.textContent?.toLowerCase() || '';
                const productDescription = card.querySelector('p, .card-text')?.textContent?.toLowerCase() || '';
                const productPrice = card.querySelector('.price, .card-price')?.textContent?.toLowerCase() || '';
                
                if (productName.includes(searchTerm.toLowerCase()) || 
                    productDescription.includes(searchTerm.toLowerCase()) ||
                    productPrice.includes(searchTerm.toLowerCase())) {
                    card.style.display = 'block';
                    card.classList.add('search-match');
                    visibleCount++;
                } else {
                    card.style.display = 'none';
                    card.classList.remove('search-match');
                }
            });
            
            // Mostrar resultado de la bÔøΩsqueda
            if (visibleCount === 0) {
                console.log('? No se encontraron productos que coincidan con:', searchTerm);
                Swal.fire({
                    icon: 'info',
                    title: 'Sin resultados',
                    text: `No se encontraron productos que coincidan con "${searchTerm}"`,
                    timer: 2000,
                    showConfirmButton: false
                });
            } else {
                console.log(`? Se encontraron ${visibleCount} productos que coinciden con:`, searchTerm);
            }
        }
        
        // Agregar event listeners para bÔøΩsqueda
        if (searchBtn) {
            searchBtn.addEventListener('click', performSearch);
        }
        
        if (searchInput) {
            searchInput.addEventListener('keypress', function(e) {
                if (e.key === 'Enter') {
                    performSearch();
                }
            });
            
            // Limpiar bÔøΩsqueda cuando estÔøΩ vacÔøΩa
            searchInput.addEventListener('input', function() {
                if (!this.value.trim()) {
                    const productCards = document.querySelectorAll('.product-card, .product-item');
                    productCards.forEach(card => {
                        card.style.display = 'block';
                        card.classList.remove('search-match');
                    });
                }
            });
        }
        
