<script>
// Funci√≥n para configurar el formulario de pago
function setupPaymentForm() {
    const form = document.getElementById('mp-form');
    if (!form) return;
    
    // Formatear n√∫mero de tarjeta
    const cardInput = form.querySelector('input[placeholder*="1234"]');
    if (cardInput) {
        cardInput.addEventListener('input', function(e) {
            let value = e.target.value.replace(/\s/g, '').replace(/[^0-9]/gi, '');
            let formattedValue = value.match(/.{1,4}/g)?.join(' ') || value;
            // Permitir hasta 19 caracteres (16 d√≠gitos + 3 espacios)
            if (value.length > 16) {
                value = value.substring(0, 16);
                formattedValue = value.match(/.{1,4}/g)?.join(' ') || value;
            }
            e.target.value = formattedValue;
        });
    }
    
    // Formatear fecha de vencimiento
    const dateInput = form.querySelector('input[placeholder*="MM/AA"]');
    if (dateInput) {
        dateInput.addEventListener('input', function(e) {
            let value = e.target.value.replace(/\D/g, '');
            if (value.length >= 2) {
                value = value.substring(0, 2) + '/' + value.substring(2, 4);
            }
            e.target.value = value;
        });
    }
    
    // Formatear CVV
    const cvvInput = form.querySelector('input[placeholder*="123"]');
    if (cvvInput) {
        cvvInput.addEventListener('input', function(e) {
            e.target.value = e.target.value.replace(/\D/g, '').substring(0, 4);
        });
    }
    
    // Manejar env√≠o del formulario
    form.addEventListener('submit', function(e) {
        e.preventDefault();
        
        // Validar formulario
        if (!form.checkValidity()) {
            e.stopPropagation();
            form.classList.add('was-validated');
            return;
        }
        
        // Simular procesamiento de pago
        const submitBtn = form.querySelector('button[type="submit"]');
        const originalText = submitBtn.innerHTML;
        
        submitBtn.innerHTML = '<div class="spinner-border spinner-border-sm me-2"></div>Procesando...';
