// üñºÔ∏è FUNCIONES DEL CARRUSEL DE IM√ÅGENES
let productImagesList = []; // Array para almacenar las im√°genes
let currentImageIndex = 0; // √çndice de la imagen actual

// Funci√≥n para manejar la subida de im√°genes
function handleImageUpload(input) {
    console.log('üì∏ Procesando im√°genes subidas...');
    
    const files = Array.from(input.files);
    
    if (files.length === 0) return;
    
    // Validar tama√±o y tipo de archivo
    const maxSize = 5 * 1024 * 1024; // 5MB
    const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif'];
    
    const validFiles = files.filter(file => {
        if (file.size > maxSize) {
            alert(`La imagen "${file.name}" es muy grande. M√°ximo 5MB.`);
            return false;
        }
        if (!allowedTypes.includes(file.type)) {
            alert(`El archivo "${file.name}" no es una imagen v√°lida.`);
            return false;
        }
        return true;
    });
    
    if (validFiles.length === 0) return;
    
    // Procesar cada imagen v√°lida
    validFiles.forEach(file => {
        const reader = new FileReader();
        reader.onload = function(e) {
            const imageData = {
                name: file.name,
                size: file.size,
                type: file.type,
                url: e.target.result,
                file: file
            };
            
            productImagesList.push(imageData);
            updateImageCarousel();
            console.log(`‚úÖ Imagen agregada: ${file.name}`);
        };
        reader.readAsDataURL(file);
    });
    
    // Limpiar el input para permitir volver a seleccionar las mismas im√°genes si es necesario
    input.value = '';
}

// Funci√≥n para actualizar el carrusel de im√°genes
function updateImageCarousel() {
    const carouselInner = document.getElementById('productImagesCarouselInner');
    const indicators = document.getElementById('productImagesIndicators');
    const prevBtn = document.querySelector('#productImagesCarousel .carousel-control-prev');
    const nextBtn = document.querySelector('#productImagesCarousel .carousel-control-next');
    
    if (!carouselInner) return;
    
    // Limpiar contenido actual
    carouselInner.innerHTML = '';
    indicators.innerHTML = '';
    
    const imageCount = productImagesList.length;
    
    if (imageCount === 0) {
        // Mostrar placeholder si no hay im√°genes
        carouselInner.innerHTML = `
            <div class="carousel-item active">
                <div class="image-placeholder">
                    <i class="fas fa-camera fa-3x text-muted"></i>
                    <p class="text-muted mt-2">Agrega im√°genes del producto</p>
                </div>
            </div>
        `;
        
        // Ocultar controles
        prevBtn.style.display = 'none';
        nextBtn.style.display = 'none';
        indicators.style.display = 'none';
        
        updateImageActionButtons();
        updateImageInfo();
        return;
    }
    
    // Crear slides para cada imagen
    productImagesList.forEach((image, index) => {
        const slideDiv = document.createElement('div');
        slideDiv.className = `carousel-item ${index === currentImageIndex ? 'active' : ''}`;
        slideDiv.innerHTML = `
            <img src="${image.url}" alt="${image.name}" class="d-block">
            <div class="carousel-caption d-none d-md-block">
                <h6>${image.name}</h6>
                <small>${(image.size / 1024).toFixed(1)} KB</small>
            </div>
        `;
        carouselInner.appendChild(slideDiv);
        
        // Crear indicador
        const indicator = document.createElement('button');
        indicator.type = 'button';
        indicator.setAttribute('data-bs-target', '#productImagesCarousel');
        indicator.setAttribute('data-bs-slide-to', index);
        indicator.className = index === currentImageIndex ? 'active' : '';
        indicator.setAttribute('aria-label', `Slide ${index + 1}`);
        if (index === currentImageIndex) {
            indicator.setAttribute('aria-current', 'true');
        }
        indicators.appendChild(indicator);
    });
    
    // Mostrar controles si hay m√°s de una imagen
    if (imageCount > 1) {
        prevBtn.style.display = 'block';
        nextBtn.style.display = 'block';
        indicators.style.display = 'flex';
    } else {
        prevBtn.style.display = 'none';
        nextBtn.style.display = 'none';
        indicators.style.display = 'none';
    }
    
    updateImageActionButtons();
    updateImageInfo();
    
    console.log(`üîÑ Carrusel actualizado: ${imageCount} im√°genes`);
}

// Funci√≥n para actualizar la informaci√≥n de im√°genes
function updateImageInfo() {
    const imageCountSpan = document.getElementById('imageCount');
    const currentIndexSpan = document.getElementById('currentImageIndex');
    
    if (imageCountSpan) {
        imageCountSpan.textContent = productImagesList.length;
    }
    
    if (currentIndexSpan) {
        currentIndexSpan.textContent = productImagesList.length > 0 ? currentImageIndex + 1 : '-';
    }
}

// Funci√≥n para actualizar los botones de acci√≥n
function updateImageActionButtons() {
    const removeBtn = document.getElementById('removeImageBtn');
    const moveLeftBtn = document.getElementById('moveLeftBtn');
    const moveRightBtn = document.getElementById('moveRightBtn');
    
    const hasImages = productImagesList.length > 0;
    const hasMultipleImages = productImagesList.length > 1;
    
    if (removeBtn) {
        removeBtn.disabled = !hasImages;
    }
    
    if (moveLeftBtn) {
        moveLeftBtn.disabled = !hasMultipleImages || currentImageIndex === 0;
    }
    
    if (moveRightBtn) {
        moveRightBtn.disabled = !hasMultipleImages || currentImageIndex === productImagesList.length - 1;
    }
}

// Funci√≥n para eliminar la imagen actual
function removeCurrentImage() {
    if (productImagesList.length === 0) return;
    
    const imageName = productImagesList[currentImageIndex].name;
    
    if (confirm(`¬øEst√°s seguro de que quieres eliminar la imagen "${imageName}"?`)) {
        productImagesList.splice(currentImageIndex, 1);
        
        // Ajustar el √≠ndice actual
        if (currentImageIndex >= productImagesList.length) {
            currentImageIndex = Math.max(0, productImagesList.length - 1);
        }
        
        updateImageCarousel();
        console.log(`üóëÔ∏è Imagen eliminada: ${imageName}`);
    }
}

// Funci√≥n para mover imagen hacia la izquierda
function moveImageLeft() {
    if (currentImageIndex > 0 && productImagesList.length > 1) {
        // Intercambiar la imagen actual con la anterior
        const temp = productImagesList[currentImageIndex];
        productImagesList[currentImageIndex] = productImagesList[currentImageIndex - 1];
        productImagesList[currentImageIndex - 1] = temp;
        
        currentImageIndex--;
        updateImageCarousel();
        console.log('‚¨ÖÔ∏è Imagen movida hacia la izquierda');
    }
}

// Funci√≥n para mover imagen hacia la derecha
function moveImageRight() {
    if (currentImageIndex < productImagesList.length - 1 && productImagesList.length > 1) {
        // Intercambiar la imagen actual con la siguiente
        const temp = productImagesList[currentImageIndex];
        productImagesList[currentImageIndex] = productImagesList[currentImageIndex + 1];
        productImagesList[currentImageIndex + 1] = temp;
        
        currentImageIndex++;
        updateImageCarousel();
        console.log('‚û°Ô∏è Imagen movida hacia la derecha');
    }
}

// Event listener para detectar cambios en el carrusel
document.addEventListener('DOMContentLoaded', function() {
    const carousel = document.getElementById('productImagesCarousel');
    if (carousel) {
        carousel.addEventListener('slide.bs.carousel', function(event) {
            currentImageIndex = event.to;
            updateImageActionButtons();
            updateImageInfo();
        });
    }
});

