// Funci√≥n para guardar el producto
function saveProduct() {
    console.log('üíæ Iniciando guardado de producto...');
    
    // Obtener datos del formulario
    const productData = {
        name: document.getElementById('productName').value.trim(),
        category: document.getElementById('productCategory').value,
        price: parseFloat(document.getElementById('productPrice').value) || 0,
        stock: parseInt(document.getElementById('productStock').value) || 0,
        gender: document.getElementById('productGender').value,
        description: document.getElementById('productDescription').value.trim(),
        status: document.getElementById('productStatus').value,
        active: document.getElementById('productActive').checked,
        images: getProductImages(),
        colors: getProductColors(),
        sizes: getProductSizes()
    };
    
    // Validaciones b√°sicas
    if (!productData.name) {
        alert('Por favor, ingresa el nombre del producto.');
        return;
    }
    
    if (!productData.category) {
        alert('Por favor, selecciona una categor√≠a.');
        return;
    }
    
    if (productData.price <= 0) {
        alert('Por favor, ingresa un precio v√°lido.');
        return;
    }
    
    if (productData.images.length === 0) {
        alert('Por favor, agrega al menos una imagen del producto.');
        return;
    }
    
    console.log('üì¶ Datos del producto:', productData);
    
    // Simular guardado (aqu√≠ se enviar√≠a al servidor)
    setTimeout(() => {
        alert('‚úÖ Producto guardado exitosamente!');
        
        // Cerrar modal
        const modal = bootstrap.Modal.getInstance(document.getElementById('ProductFormModal'));
        if (modal) {
