        clearProductForm();
        
        console.log('‚úÖ Producto guardado y formulario limpiado');
    }, 1000);
}

// Funci√≥n para obtener colores del formulario
function getProductColors() {
    const colors = [];
    const colorInputs = document.querySelectorAll('.color-input');
    const colorPickers = document.querySelectorAll('.color-picker');
    
    colorInputs.forEach((input, index) => {
        const colorName = input.value.trim();
        const colorValue = colorPickers[index]?.value || '#000000';
        
        if (colorName) {
            colors.push({
                name: colorName,
                value: colorValue
            });
        }
    });
    
    return colors;
}

// Funci√≥n para obtener tallas del formulario
function getProductSizes() {
    const sizes = [];
    const sizeInputs = document.querySelectorAll('.size-input');
    const stockInputs = document.querySelectorAll('#sizesContainer input[type="number"]');
    
    sizeInputs.forEach((input, index) => {
        const sizeName = input.value.trim();
        const sizeStock = parseInt(stockInputs[index]?.value) || 0;
        
        if (sizeName) {
            sizes.push({
                name: sizeName,
                stock: sizeStock
            });
        }
    });
    
    return sizes;
}
