<script>
document.addEventListener('DOMContentLoaded', function() {
    console.log('üîß Configurando modales sin backdrop...');
    
    // Interceptar la creaci√≥n de nuevos modales Bootstrap
    const originalModal = bootstrap.Modal;
    bootstrap.Modal = function(element, options = {}) {
        // Forzar backdrop: false para todos los modales
        options.backdrop = false;
        options.keyboard = true;
        console.log('üö´ Modal creado sin backdrop:', element.id || 'sin-id');
        return new originalModal(element, options);
    };
    
    // Limpiar cualquier backdrop existente
    function removeExistingBackdrops() {
        const backdrops = document.querySelectorAll('.modal-backdrop');
        backdrops.forEach(backdrop => {
            console.log('üóëÔ∏è Removiendo backdrop existente');
            backdrop.remove();
        });
        
        // Restaurar scroll del body
        document.body.classList.remove('modal-open');
        document.body.style.overflow = '';
        document.body.style.paddingRight = '';
    }
    
    // Ejecutar limpieza inicial
    removeExistingBackdrops();
    
    // Limpiar backdrops cada vez que se muestre un modal
    document.addEventListener('show.bs.modal', function(e) {
        console.log('üì± Modal mostrandose:', e.target.id);
        setTimeout(removeExistingBackdrops, 100);
    });
    
    // Limpiar backdrops cuando se oculte un modal
    document.addEventListener('hide.bs.modal', function(e) {
        console.log('‚ùå Modal ocultandose:', e.target.id);
        setTimeout(removeExistingBackdrops, 100);
    });
    
    // Limpiar backdrops despu√©s de que se oculte completamente
    document.addEventListener('hidden.bs.modal', function(e) {
        console.log('‚úÖ Modal oculto completamente:', e.target.id);
        setTimeout(removeExistingBackdrops, 100);
    });
    
    console.log('‚úÖ Configuraci√≥n de modales sin backdrop completada');
});

// ‚úÖ FUNCI√ìN DE RECUPERACI√ìN PARA PROCEEDTOPAYMENT
document.addEventListener('DOMContentLoaded', function() {
    // Verificar si la funci√≥n existe
    if (typeof window.proceedToPayment !== 'function') {
        console.log('üö® Recuperando funci√≥n proceedToPayment...');
        
        window.proceedToPayment = function() {
            console.log('üöÄ [RECUPERACI√ìN] Ejecutando proceedToPayment...');
            
            try {
                // Verificar carrito con ambas claves posibles
                const carrito1 = JSON.parse(localStorage.getItem('carrito') || '[]');
                const carrito2 = JSON.parse(localStorage.getItem('cart') || '[]');
                const cart = carrito1.length > 0 ? carrito1 : carrito2;
                
                console.log('üõí Productos en carrito:', cart.length);
                
                if (cart.length === 0) {
                    alert('No hay productos en el carrito para proceder al pago');
                    return;
                }
                
                // Cerrar modal del carrito
                const cartModal = document.getElementById('CartModal');
                if (cartModal) {
                    cartModal.style.display = 'none';
                    cartModal.classList.remove('show');
                    document.body.classList.remove('modal-open');
                    const backdrop = document.querySelector('.modal-backdrop');
                    if (backdrop) backdrop.remove();
                }
                
                // Redirigir inmediatamente
                console.log('‚úÖ Redirigiendo a pago-premium.html...');
                window.location.href = 'pago-premium.html';
                
            } catch (error) {
                console.error('‚ùå Error en proceedToPayment:', error);
                alert('Error al procesar el pago. Int√©ntalo nuevamente.');
            }
        };
        
        console.log('‚úÖ Funci√≥n proceedToPayment recuperada');
    } else {
        console.log('‚úÖ Funci√≥n proceedToPayment ya existe');
    }
    
    console.log('üîß Estado final:', typeof window.proceedToPayment);
});

// ‚úÖ INICIALIZACI√ìN DEL SISTEMA DE B√öSQUEDA MEJORADO - SIN LOOPS INFINITOS
document.addEventListener('DOMContentLoaded', function() {
    console.log('üîç Inicializando sistema de b√∫squeda mejorado...');
    
    // Variable para evitar m√∫ltiples inicializaciones
    if (window.searchSystemInitialized) {
        console.log('‚ö†Ô∏è Sistema de b√∫squeda ya inicializado, omitiendo...');
        return;
    }
    
    // Configurar b√∫squeda en tiempo real
    setupRealTimeSearch();
    
    // ‚úÖ CONFIGURAR MANEJO DE IM√ÅGENES FALTANTES
    setupImageErrorHandling();
    
    // Marcar productos como buscables agregando atributos (UNA SOLA VEZ)
    setTimeout(() => {
        const allProductCards = document.querySelectorAll('.product-card:not([data-search-configured]), .api-product:not([data-search-configured]), .main-product-card:not([data-search-configured])');
        
        if (allProductCards.length === 0) {
            console.log('‚ö†Ô∏è No hay productos nuevos para configurar');
            return;
        }
        
        allProductCards.forEach((card, index) => {
            // Marcar como configurado para evitar reconfiguraci√≥n
            card.setAttribute('data-search-configured', 'true');
            
            // Obtener nombre del producto
            const nameElement = card.querySelector('.product-name, .card-title, h5, h6');
            if (nameElement) {
                card.setAttribute('data-product-name', nameElement.textContent.trim());
            }
            
            // Obtener categor√≠a
            const categoryElement = card.querySelector('.category, .badge, [data-category]');
            if (categoryElement) {
                const category = categoryElement.textContent || categoryElement.getAttribute('data-category');
                if (category) {
                    card.setAttribute('data-category', category.trim());
                }
            }
            
            console.log(`üè∑Ô∏è Producto ${index + 1} configurado para b√∫squeda:`, card.getAttribute('data-product-name'));
        });
        
        console.log(`‚úÖ ${allProductCards.length} productos NUEVOS configurados para b√∫squeda`);
        
    }, 3000); // Esperar a que se carguen los productos din√°micos
    
    // Marcar como inicializado
    window.searchSystemInitialized = true;
    console.log('‚úÖ Sistema de b√∫squeda inicializado correctamente');
});

// ‚úÖ FUNCI√ìN PARA MANEJAR ERRORES DE IM√ÅGENES
function setupImageErrorHandling() {
    console.log('üñºÔ∏è Configurando manejo de errores de im√°genes...');
    
    // Configurar manejo de errores para im√°genes existentes
    document.querySelectorAll('img.product-image, img.card-img-top').forEach(img => {
        img.addEventListener('error', function() {
            console.log('‚ùå Error cargando imagen:', this.src);
            
            // Evitar loop infinito de errores
            if (!this.classList.contains('placeholder-image')) {
                this.style.display = 'none';
                
                // Crear div placeholder
                const placeholder = document.createElement('div');
                placeholder.className = 'placeholder-image';
                placeholder.style.width = this.offsetWidth + 'px' || '100%';
                placeholder.style.height = this.offsetHeight + 'px' || '200px';
                
                // Insertar placeholder despu√©s de la imagen
                this.parentNode.insertBefore(placeholder, this.nextSibling);
                
                console.log('‚úÖ Placeholder creado para imagen faltante');
            }
        });
    });
    
    // Sistema simplificado sin observer para evitar loops infinitos
    console.log('‚úÖ Sistema de im√°genes configurado sin observers autom√°ticos');
    
    console.log('‚úÖ Manejo de errores de im√°genes configurado');
}
</script>

